--
-- Cấu trúc bảng cho bảng `tbl_product`
--

CREATE TABLE `tbl_product` (
  `product_id` int(10) UNSIGNED NOT NULL,
  `category_id` int(11) NOT NULL,
  `brand_id` int(11) NOT NULL,
  `product_name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_desc` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_content` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_price` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_price_sale` varchar(60) COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_image` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_stock` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_status` int(11) NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `tbl_product`
--

INSERT INTO `tbl_product` (`product_id`, `category_id`, `brand_id`, `product_name`, `product_desc`, `product_content`, `product_price`, `product_price_sale`, `product_image`, `product_stock`, `product_status`, `created_at`, `updated_at`) VALUES
(10, 13, 4, 'Combo Giáo Trình Hán Ngữ 1 Tập 1 Quyển Thượng Và Tập Viết Chữ Hán Theo GTHN- Kèm App Học Online', '“Combo Giáo Trình Hán Ngữ 1 Tập 1 Và Tập Viết Chữ Hán” là bộ sách mà bạn không thể bỏ qua nếu muốn giao tiếp tiếng Trung thành thạo hay ôn thi các chứng chỉ tiếng Trung.\r\n\r\nKhi mua sách tại MCBooks:\r\n-	Sách có kèm bài luyện nghe tích hợp trên App học online MCBooks\r\n-	Tặng ngay Bookmark để dễ dàng ghi nhớ phần đã học tới của sách.\r\n-	Tặng ngay Voucher giảm giá 5% trong vòng 5 năm khi sử dụng sách của MCBooks.\r\n-	Sách được bọc túi chống sốc và hộp giấy cẩn thận để tránh nhăn quăn sách.\r\n-	Lưu ý: Chỉ có sách chính hãng của MCBooks mới có thể quét được mã QR-code cho khóa học online và các file nghe đi kèm với sách.\r\n\r\n1.   Cuốn sách “Giáo Trình Hán Ngữ 1 Tập 1 Quyển Thượng Phiên Bản Mới” giúp bạn:\r\n-	Đa dạng ngôn ngữ\r\n-	Học theo chủ đề\r\n-	Bổ sung phương pháp luyện tập qua băng ghi âm\r\n-	Cung cấp vốn hiểu biết về văn hóa và con người Trung Hoa', 'Nội dung cuốn sách:\r\n-	Cuốn sách Giáo trình Hán ngữ tập 1 – Quyển Thượng (phiên bản mới) bao gồm 15 bài học, mỗi bài học bao gồm: bài khóa, từ mới, chú thích, ngữ pháp, ngữ âm và luyện tập những kiến thức đã học.\r\n\r\nThông tin sản phẩm\r\n-	Tác giả: Đại học Ngôn ngữ Bắc Kinh\r\n-	Nhà xuất bản: NXB Đại học Quốc gia Hà Nội\r\n-	Công ty phát hành: MCBooks\r\n-	Số trang: 176\r\n-	Năm xuất bản: 2019\r\n-	Hình thức: Bìa mềm\r\n\r\n2.   Nội dung của sách \"Tập viết chữ Hán - Dựa theo giáo trình Hán ngữ phiên bản mới\" gồm:\r\n-          Phần 1: Danh mục bộ thủ chữ Hán: cung cấp cho các bạn danh mục 214 bộ thủ chữ Hán, không chỉ bao gồm cách viết, tên bộ, phiên âm và ý nghĩa tiếng Việt.\r\n-          Phần 2: Kết cấu chữ Hán\r\n-          Phần 3: Bao gồm 40 bài tập viết chữ hán\r\nMỗi bài sẽ giới thiệu cho bạn từ 2-6 bộ thủ mới (bao gồm âm hán Việt, cách viết chữ từng nét,). Tiếp đó là các cụm từ mới có bộ thủ đó bao gồm cả phiên âm và nghĩa tiếng việt chi tiết giúp các bạn luyện tập và ghi nhớ bộ thủ, đồng thời thu thập thêm nhiều từ vựng mới.\r\n-          Phần 4: 500 chữ Hán cơ bản: Tại phần này sẽ cung cấp cho bạn 500 chữ Hán cơ bản gồm cả chữ phồn thể và giản thể , xếp thứ tự theo phiên âm Pinyin. Mỗi chứ Hán được giải thích rõ về HÌNH-ÂM-NGHĨA và cách viết (tả pháp).\r\n\r\nCuốn sách sẽ là người bạn đồng hành thân thiết, hỗ trợ đắc lực cho các bạn yêu thích và muốn học tốt tiếng Trung.\r\n\r\nThông tin sản phẩm\r\n-        Công ty phát hành: MCBOOKS\r\n-        Ngày xuất bản: 09-2019\r\n-        Tác giả: Chu Hồng Hạnh (Chủ biên)\r\n-        Nhà xuất bản: Nhà Xuất Bản Hồng Đức\r\n-        Loại bìa:	Bìa mềm', '140000', '127440', 'Combo Giáo Trình Hán Ngữ 1 Tập 1 Quyển Thượng Và Tập Viết Chữ Hán Theo GTHN- Kèm App Học Online30.jpeg', '', 1, NULL, NULL),
(11, 13, 4, 'Combo Giáo Trình Hán Ngữ Tập 1 (Quyển Thượng và Quyển Hạ) Và Tập Viết Chữ Hán Theo Giáo Trình', '“Combo Giáo Trình Hán Ngữ Tập 1 (Quyển Thượng và Quyển Hạ) Và Tập Viết Chữ Hán Theo Giáo Trình” là bộ sách tiếng Trung cung cấp những kiến thức nền tảng nhất cho các bạn muốn học tiếng Trung từ con số 0.\r\n\r\nCombo sách bao gồm:\r\n-    Giáo Trình Hán Ngữ Tập 1 (Quyển Thượng và Quyển Hạ)\r\n-    Tập viết chữ Hán theo giáo trình.', 'Nội dung của từng cuốn:\r\nGiáo Trình Hán Ngữ 1 (Tập 1) - Quyển Thượng \r\n\r\nBộ Giáo Trình Hán Ngữ gồm nhiều bài với nội dung từ dễ đến khó. Bắt đầu từ luyện tập ngữ âm cơ bản tiếng Hán, từ đó tiến hành giảng dạy kết cấu ngữ pháp, ngữ nghĩa và ngữ dụng, cuối cùng là giai đoạn giảng dạy ngữ đoạn, thông qua sự hiểu biết sâu hơn về ngữ pháp và vận dụng từ ngữ, để nâng cao hơn nữa khả năng biểu đạt thành đoạn văn của người học.\r\nGiáo Trình Hán Ngữ có 3300 từ mới. Phần bài khóa và nội dung đàm thoại có quan hệ chủ điểm đồng nhất với bài luyện đọc trong phần bài tập, trên cơ sở bảng từ mới của từng bài. Đặc biệt, cuốn sách học cùng app MCBooks ghi giọng đọc chuẩn giúp bạn luyện nghe và học phát âm hiệu quả.\r\n\r\nGiáo Trình Hán Ngữ 2 (Tập 1): Quyển Hạ \r\n\r\nGiáo trình Hán ngữ mới với sự thay đổi về thứ tự các bài học, phần giải thích, từ mới, nội dung... đã được tác giả bổ sung và đổi mới, do vậy người học sẽ có thể tiếp thu những kiến thức mới hơn, hiệu quả hơn và thành tích học tập sẽ được nâng cao hơn.\r\n\r\nBộ giáo trình Hán ngữ mới này gồm 76 bài với nội dung từ dễ đến khó. Bắt đầu từ luyện tập ngữ âm cơ bản tiếng Hán, từ đó tiến hành giảng dạy kết cấu ngữ pháp, ngữ nghĩa và ngữ dụng; cuối cùng là giai đoạn giảng dạy ngữ đoạn, thông qua sự hiểu biết sâu hơn về ngữ pháp và vận dụng từ ngữ, để nâng cao hơn nữa khả năng biểu đạt thành đoạn văn của người học.\r\n\r\nCuốn sách “Tập viết chữ Hán” dày 118 trang bao gồm những phần nội dung sau:\r\n-	Phần 1: Danh mục bộ thủ chữ hán \r\n-	Phần 2: Kết cấu chữ Hán\r\n-	Phần 3: Tập viết chữ Hán\r\n-	Kèm phần phụ lục 500 chữ Hán cơ bản HÌNH – ÂM – NGHĨA – TẢ PHÁP\r\n\r\nKhi mua sách tại MCBooks:\r\n-    Học kèm app học online thông minh, tiện lợi.\r\n-    Tặng ngay Bookmark để dễ dàng ghi nhớ phần đã học tới của sách.\r\n-    Tặng ngay Voucher giảm giá 5% trong vòng 5 năm khi sử dụng sách của MCBooks.\r\n-    Sách được bọc túi chống sốc và hộp giấy cẩn thận để tránh nhăn quăn sách.\r\nLưu ý: Chỉ có sách chính hãng của MCBooks mới có thể quét được mã QR-code cho khóa học online và các file nghe đi kèm với sách.\r\n\r\nThông tin sản phẩm 1 :\r\n-	Tác giả: Đại học Ngôn ngữ Bắc Kinh\r\n-	Nhà xuất bản: NXB Đại học Quốc gia Hà Nội\r\n-	Công ty phát hành: MCBooks\r\n-	Số trang: 176\r\n-	Năm xuất bản: 2019\r\n-	Hình thức: Bìa mềm\r\n\r\nThông tin sản phẩm 2 :\r\n-	Tác giả: Đại học Ngôn ngữ Bắc Kinh\r\n-	Nhà xuất bản: NXB Đại học Quốc gia Hà Nội\r\n-	Công ty phát hành: MCBooks\r\n-	Số trang: 231\r\n-	Năm xuất bản: 2019\r\n-	Hình thức: Bìa mềm\r\n\r\nThông tin sản phẩm 2 :\r\n-	Tác giả: Đại học Ngôn ngữ Bắc Kinh\r\n-	Nhà xuất bản: NXB Đại học Quốc gia Hà Nội\r\n-	Công ty phát hành: MCBooks\r\n-	Số trang: 101\r\n-	Năm xuất bản: 2019\r\n-	Hình thức: Bìa mềm', '230000', '210240', 'Combo Giáo Trình Hán Ngữ Tập 1 (Quyển Thượng và Quyển Hạ) Và Tập Viết Chữ Hán Theo Giáo Trình81.jpeg', '', 1, NULL, NULL),
(12, 13, 4, 'Combo Giáo Trình Hán Ngữ Tập 1 (Quyển Thượng và Quyển Hạ)', '\"Combo Giáo Trình Hán Ngữ Tập 1 (Quyển Thượng và Quyển Hạ)\" được đánh giá là bộ sách đầy tinh tế và chọn lọc giúp các bạn mới bắt đầu học tiếng trung nắm được hơn 1000 từ cơ bản và các hạng mục ngữ pháp sơ cấp để học lên trình độ cao hơn.\r\n\r\nHiện nay, tiếng trung vẫn đang là một ngôn ngữ được nhiều người theo học. Tuy nhiên, mọi người học trung đều gặp phải những khó khăn như:\r\n-    Bản chất chất tiếng trung khó để có thể nhớ mặt chữ\r\n-    Chưa có phương pháp học hiệu quả\r\n-    Chưa tìm được tài liệu chuẩn hướng dẫn học\r\n-    Chưa có lộ trình rõ ràng \r\n\r\nThấu hiểu được những khó khăn của những bạn học tiếng. Trong nhiều năm nghiên cứu học hỏi, tác giả đã cho xuất bản “ Combo Giáo Trình Hán Ngữ Tập 1 (Quyển Thượng và Quyển Hạ)”. Cuốn sách giúp bạn đọc:\r\n-    Có được tài liệu chuẩn nhất để tham khảo\r\n-    Cung cấp bài học theo lộ trình, giúp bạn học định hướng được con đường học tập\r\n-    Tổng hợp những kiến thức cần thiết nhất khi học tiếng trung\r\n-    Cung cấp phương pháp học hiệu quả\r\n-    Sau khi hoàn thành 2 cuốn có thể đạt mức HSK 2.', 'Nội dung của “Combo Giáo Trình Hán Ngữ Tập 1”:\r\n-    Giáo trình Hán ngữ tập 1 – Quyển Thượng bao gồm 15 bài học, mỗi bài học bao gồm: bài khóa, từ mới, chú thích, ngữ pháp, ngữ âm và luyện tập những kiến thức đã học.\r\n\r\n-    Giáo trình Hán ngữ tập 1- Quyển Hạ cũng gồm 15 bài học, nhưng có độ khó cao hơn so với quyển Thượng.\r\n\r\n\"Combo Giáo Trình Hán Ngữ Tập 1 (Quyển Thượng và Quyển Hạ)\" là bộ sách hữu ích không thể thiếu dành cho các bạn đang muốn học tiếng Trung. Chúc các bạn học tiếng Trung hiệu quả.\r\n\r\nKhi mua sách tại MCBooks:\r\n-    Học kèm file nghe qua app học thông minh, tiện lợi.\r\n-    Tặng ngay Bookmark để dễ dàng ghi nhớ phần đã học tới của sách.\r\n-    Tặng ngay Voucher giảm giá 5% trong vòng 5 năm khi sử dụng sách của MCBooks.\r\n-    Sách được bọc túi chống sốc và hộp giấy cẩn thận để tránh nhăn quăn sách.\r\nLưu ý: Chỉ có sách chính hãng của MCBooks mới có thể quét được mã QR-code cho khóa học online và các file nghe đi kèm với sách.\r\n\r\nThông tin sản phẩm :\r\n-	Tác giả: Đại học Ngôn ngữ Bắc Kinh\r\n-	Nhà xuất bản: NXB Đại học Quốc gia Hà Nội\r\n-	Công ty phát hành: MCBooks\r\n-	Số trang: 176 (quyển 1) ; 231 (quyển 2).\r\n-	Năm xuất bản: 2019\r\n-	Hình thức: Bìa mềm', '150000', '146880', 'Combo Giáo Trình Hán Ngữ Tập 1 (Quyển Thượng và Quyển Hạ)80.jpeg', '', 1, NULL, NULL),
(13, 13, 4, 'Combo Giáo Trình Hán Ngữ Tập 3 (Quyển Thượng và Quyển Hạ)', '“Combo Giáo Trình Hán Ngữ Tập 3 (Quyển Thượng và Quyển Hạ)” là bộ sách hay, chọn lọc kiến thức thức giúp bạn chinh phục được trình độ cao nhất trong thang bậc tiếng Trung.\r\n\r\nKhi mua sách tại MCBooks:\r\n-    Học kèm app học online thông minh, tiện lợi.\r\n-    Tặng ngay Bookmark để dễ dàng ghi nhớ phần đã học tới của sách.\r\n-    Tặng ngay Voucher giảm giá 5% trong vòng 5 năm khi sử dụng sách của MCBooks.\r\n-    Sách được bọc túi chống sốc và hộp giấy cẩn thận để tránh nhăn quăn sách.\r\nLưu ý: Chỉ có sách chính hãng của MCBooks mới có thể quét được mã QR-code cho khóa học online và các file nghe đi kèm với sách.\r\n\r\nĐối với những bạn đã học qua Giáo trình Hán ngữ Tập 2, căn bản hiện tại các bạn đã nắm chắc hơn 2500 từ vựng và đạt được mức HSK 4. Tuy nhiên, tiếng Trung không chỉ dừng lại ở cấp bậc thứ 4 mà còn nhiều cấp bậc cao hơn (hiện tại là HSK 6).Combo Giáo Trình Hán Ngữ Tập 3 (Quyển Thượng và Quyển Hạ) Để chinh phục và thấm sâu được sự hay của tiếng Trung thì bạn cần thời gian, sức lực,  chinh phục chinh độ cao nhất cũng như phục vụ cho mục đích du học và làm việc.\r\n\r\nĐể giúp các bạn hoàn thiện hơn về kiến thức cũng như có được tài liệu chuẩn nhất thì “ Combo Giáo Trình Hán Ngữ Tập 3 (Quyển Thượng và Quyển Hạ)” được xuất bản giúp các bạn:\r\n-	Có được tài liệu chuẩn nhất để tiếp tục chinh phục tiếng Trung ở mức cao hơn.\r\n-	Bổ sung vốn từ vựng với hơn 3300 từ mới. \r\n-	Cung cấp bài học theo lộ trình, giúp bạn học định hướng được con đường học tập tiếp theo.\r\n-	Tổng hợp những kiến thức cần thiết nhất khi học tiếng trung.\r\n-	Đạt HSK 5 -6 sau khi hoàn thành và nắm chắc kiến thức của bộ sách.', 'Nội dung của “Combo Giáo Trình Hán Ngữ Tập 3 (Quyển Thượng):\r\n-	Bộ Giáo Trình Hán Ngữ gồm nhiều bài với nội dung từ dễ đến khó. Bắt đầu từ luyện tập ngữ âm cơ bản tiếng Hán, từ đó tiến hành giảng dạy kết cấu ngữ pháp, ngữ nghĩa và ngữ dụng, cuối cùng là giai đoạn giảng dạy ngữ đoạn, thông qua sự hiểu biết sâu hơn về ngữ pháp và vận dụng từ ngữ, để nâng cao hơn nữa khả năng biểu đạt thành đoạn văn của người học.\r\n\r\nThông tin sản phẩm\r\n-	Tác giả: Đại học Ngôn ngữ Bắc Kinh\r\n-	Nhà xuất bản: Nhà Xuất Bản Đại Học Quốc Gia Hà Nội\r\n-	Công ty phát hành: MCBooks\r\n-	Số trang: 373\r\n-	Năm xuất bản: 2015\r\n-	Hình thức: Bìa mềm\r\n\r\nCuốn “Giáo Trình Hán Ngữ Tập 3 (Quyển Hạ):\r\n-	Bài học chủ điểm giúp người học làm quen với cách sử dụng tiếng Trung trong cuộc sống hàng ngày, tạo nền tảng cho học viên học giao tiếp đơn thuần hoặc ôn tập để thi các chứng chỉ tiếng Trung.\r\n-	Trong mỗi bài học chủ điểm, tác giả xây dựng nội dung bài học bao gồm 6 phần: Bài khóa, Từ mới, Chú thích, Ngữ pháp, Ngữ âm, Luyện tập giúp bạn nâng cao cả 4 kỹ năng Nghe – Nói – Đọc – Viết tiếng Trung.\r\n\r\nThông tin sản phẩm\r\n-	Tác giả: Đại học Ngôn ngữ Bắc Kinh\r\n-	Nhà xuất bản: Nhà Xuất Bản Đại Học Quốc Gia Hà Nội\r\n-	Công ty phát hành: MCBooks\r\n-	Số trang: 219\r\n-	Năm xuất bản: 2015\r\n-	Hình thức: Bìa mềm', '170000', '154080', 'Combo Giáo Trình Hán Ngữ Tập 3 (Quyển Thượng và Quyển Hạ)89.jpeg', '', 1, NULL, NULL),
(14, 13, 4, 'Giáo Trình Hán Ngữ 1 - Tập 1 Quyển Thượng - Học Kèm App MCBooks', 'MÔ TẢ SẢN PHẨM\r\n“Giáo Trình Hán Ngữ 1 Tập 1 Quyển Thượng Phiên Bản Mới” là cuốn sách mà bạn không thể bỏ qua nếu muốn giao tiếp tiếng Trung thành thạo hay ôn thi các chứng chỉ tiếng Trung.\r\n\r\nCuốn sách “Giáo Trình Hán Ngữ 1 Tập 1 Quyển Thượng Phiên Bản Mới” giúp bạn:\r\n-	Đa dạng ngôn ngữ\r\n-	Học theo chủ đề\r\n-	Bổ sung phương pháp luyện tập qua băng ghi âm\r\n-	Cung cấp vốn hiểu biết về văn hóa và con người Trung Hoa', 'Nội dung cuốn sách:\r\n-	Cuốn sách Giáo trình Hán ngữ tập 1 – Quyển Thượng (phiên bản mới) bao gồm 15 bài học, mỗi bài học bao gồm: bài khóa, từ mới, chú thích, ngữ pháp, ngữ âm và luyện tập những kiến thức đã học.\r\n\r\nThông tin sản phẩm\r\n-	Tác giả: Đại học Ngôn ngữ Bắc Kinh\r\n-	Nhà xuất bản: NXB Đại học Quốc gia Hà Nội\r\n-	Công ty phát hành: MCBooks\r\n-	Số trang: 176\r\n-	Năm xuất bản: 2019\r\n-	Hình thức: Bìa mềm\r\n\r\nKhi mua sách tại MCBooks:\r\n-	Sách có kèm bài luyện nghe tích hợp trên App học online MCBooks\r\n-	Tặng ngay Bookmark để dễ dàng ghi nhớ phần đã học tới của sách.\r\n-	Tặng ngay Voucher giảm giá 5% trong vòng 5 năm khi sử dụng sách của MCBooks.\r\n-	Sách được bọc túi chống sốc và hộp giấy cẩn thận để tránh nhăn quăn sách.\r\n-	Lưu ý: Chỉ có sách chính hãng của MCBooks mới có thể quét được mã QR-code cho khóa học online và các file nghe đi kèm với sách.', '96000', '64080', 'Giáo Trình Hán Ngữ 1 - Tập 1 Quyển Thượng - Học Kèm App MCBooks11.jpeg', '', 1, NULL, NULL),
(16, 13, 4, 'Giáo Trình Hán Ngữ 5 - Tập 3 Quyển Thượng Bổ Sung Bài Tập Và Đáp Án ( Gồm CD )', 'MÔ TẢ SẢN PHẨM\r\nCũng như bất kỳ ngôn ngữ nào mà bạn đọc có nhu cầu muốn sử dụng thành thạo như ngôn ngữ thứ hai, học tiếng Trung cũng đòi hỏi bạn có nhiều nỗ lực, phương pháp học và tài liệu học thực sự phù hợp.\r\n5 câu hỏi thường gặp với những bạn có nhu cầu tìm hiểu và học tiếng Trung\r\nNgười mới bắt đầu tiếp cận tiếng Trung thì học như thế nào?\r\nĐâu là phương pháp học tối ưu thời gian và đạt hiệu quả thực tế?\r\nHọc tiếng Trung khi đã lớn liệu có khó khăn nhiều không?\r\nLựa chọn tài liệu học nào trong vô số tài liệu có mặt trên thị trường?\r\nLàm thế nào để phát triển đồng thời cả 4 kỹ năng Nghe – Nói – Đọc – Viết?\r\nNhằm giúp bạn học tiếp cận những nghiên cứu về tri thức và phương pháp học hiện đại, 76 bài học được đúc kết trong bộ 6 cuốn sách Giáo trình Hán ngữ phiên bản mới với nội dung từ cơ bản đến nâng cao giúp người học có thể nắm chắc những kiến thức từ ngữ âm cơ bản đến khả năng biểu đạt ngôn ngữ tiếng Hán giao tiếp thông dụng.', 'Cuốn sách Giáo trình Hán ngữ 5 tập 3 – Quyển Thượng (phiên bản mới) bao gồm 13 bài học, mỗi bài học bao gồm:\r\nBài khóa\r\nTừ mới\r\nChú thích\r\nNgữ pháp, ngữ âm\r\nLuyện tập những kiến thức đã học.\r\nNguồn kiến thức bạn tiếp nhận được từ cuốn sách Giáo trình Hán ngữ 5 tập 3 – Quyển Thượng (phiên bản mới)\r\nĐa dạng ngôn ngữ\r\nKhác với những cuốn sách học tiếng Hán thông thường, cuốn sách không chỉ cung cấp những kiến thức ngữ pháp, ngữ âm hay từng vựng tiếng Hán kèm theo những giải thích tiếng Việt, cuốn sách còn cung cấp những giải thích bằng tiếng Anh giúp tiếp cận đa dạng đối tượng sử dụng sách. Đồng thời, với cách tiếp cận này cũng giúp bạn đọc đã có nền tảng tiếng Anh tốt được vận dụng sử dụng linh hoạt tiếng Hán.\r\nHọc theo chủ đề\r\nMột trong những phương pháp học phổ biến được đánh giá cao nhất hiện nay là phương pháp học theo chủ đề. Với mục đích giúp bạn đọc có thể giao tiếp tốt, cuốn sách cung cấp các bài học theo chủ đề thông dụng trong cuộc sống hàng ngày. Với những tình huống thường nhật, mỗi bài học bao gồm những mẫu câu, từ vựng, cách phát âm,… giúp dễ dàng ứng dụng trong thực tế giao tiếp.\r\nBổ sung phương pháp luyện tập qua băng ghi âm\r\nĐể giúp người học tiếp xúc với âm điệu, âm sắc tiếng Trung một cách thực tế nhất, giáo trình cung cấp bộ băng ghi âm được thực hiện bởi các chuyên gia ngôn ngữ nổi tiếng, giàu kinh nghiệm của Trung Quốc với âm điệu chuẩn xác, diễn cảm giúp việc học tập hiệu quả nhất có thể.\r\nCung cấp vốn hiểu biết về văn hóa và con người Trung Hoa\r\nCác bài học xoay quanh những vấn đề và con người Trung Hoa, do đó, với cuốn sách này bạn không chỉ tiếp cận và giao tiếp tiếng Trung mà còn có cơ hội tìm hiểu thêm về con người và nền văn hóa Trung Hoa.\r\nVới những sửa đổi và bổ sung phù hợp với phương pháp học hiện đại, cuốn sách Giáo trình Hán ngữ 5 tập 3 – Quyển Thượng (phiên bản mới) nói riêng và bộ sách Giáo trình Hán ngữ (phiên bản mới) nói chung sẽ là bộ sách ưu tú giúp bạn cải thiện và nâng cao vốn ngôn ngữ tiếng Trung hiện đại.', '120000', '115000', 'Giáo Trình Hán Ngữ 5 - Tập 3 Quyển Thượng Bổ Sung Bài Tập Và Đáp Án ( Gồm CD )55.jpeg', '', 1, NULL, NULL),
(17, 13, 4, 'Giáo Trình Phát Triển Hán Ngữ Nói Giao Tiếp Sơ Cấp 1 - Học Kèm App Online', 'MÔ TẢ SẢN PHẨM\r\n\" Giáo Trình Phát Triển Hán Ngữ Nói Giao Tiếp Sơ Cấp 1” chủ yếu dùng cho sinh viên học tiếng Trung Quốc, có thể đáp ứng được yêu cầu giảng dạy của môn học chính ở các cấp độ như sơ cấp (bao gồm mới bắt đầu).\r\n\r\nCuốn sách “Giáo Trình Phát Triển Hán Ngữ Nói Giao Tiếp Sơ Cấp 1” giúp bạn:\r\n-	Nâng cao khả năng nói – giao tiếp tiếng Trung, sử dụng tiếng Trung như một công cụ giao tiếp  để vận dụng ngôn ngữ hiệu quả trong thực tế giao tiếp hàng ngày.\r\n-	Người học vừa có thể theo sát nội dung được cung cấp trong tài liệu, vừa có thể tích cực, chủ động nắm bắt, trải nghiệm và tiếp thu ứng dụng ngôn ngữ từ trong cuộc sống hàng ngày. Người học dành thời gian trải nghiệm, ghi nhớ và vận dụng những nội dung được học vào thực tế, tự tạo cảm hứng học tập cho bản thân.\r\n-	Giáo viên dựa trên nghiên cứu tài liệu, xây dựng phương hướng giảng dạy phù hợp và có thể dựa vào đó để tự bổ sung và phát triển bản thân.\r\n-	Luyện nghe và phát âm chuẩn như người bản ngữ.', 'Nội dung cuốn sách:\r\n-	“Giáo Trình Phát Triển Hán Ngữ Nói Giao Tiếp Sơ Cấp 1” được biên soạn bởi các chuyên gia hàng đầu về Giáo dục Hán ngữ Quốc tế của Trường Đại học Ngôn ngữ Bắc Kinh được ứng dụng giảng dạy rộng rãi tại các trường học của Trung Quốc và hiện đang được một số trường ĐH ở Việt Nam sử dụng. \r\n-	Với những cập nhật về tư duy dạy và học tiên tiến nhất, cũng như nội dung và hình thức mang tính ứng dụng cao, phù hợp với xã hội hiện đại, “Giáo trình Phát triển Hán ngữ” giúp bạn nhanh chóng tự Phát triển tiếng Trung một cách có hệ thống và hiệu quả, để bạn có thể SỬ DỤNG GIAO TIẾP và có đủ hệ thống kiến thức và năng lực THI HSK.\r\n\r\nKhi mua sách tại MCBooks:\r\n-	Sách có kèm bài luyện nghe tích hợp trên App học online MCBooks\r\n-	Tặng ngay Bookmark để dễ dàng ghi nhớ phần đã học tới của sách.\r\n-	Tặng ngay Voucher giảm giá 5% trong vòng 5 năm khi sử dụng sách của MCBooks.\r\n-	Sách được bọc túi chống sốc và hộp giấy cẩn thận để tránh nhăn quăn sách.\r\n-	Lưu ý: Chỉ có sách chính hãng của MCBooks mới có thể quét được mã QR-code cho các file nghe đi kèm với sách.\r\n\r\nThông tin sản phẩm\r\n-	Tác giả: Đại học Ngôn ngữ Bắc Kinh\r\n-	Nhà xuất bản: NXB Hồng Đức\r\n-	Công ty phát hành: MCBooks\r\n-	Số trang: 292\r\n-	Năm xuất bản: 2019\r\n-	Hình thức: Bìa mềm', '124960', '120960', 'Giáo Trình Phát Triển Hán Ngữ Nói Giao Tiếp Sơ Cấp 1 - Học Kèm App Online80.jpeg', '', 1, NULL, NULL),
(18, 13, 4, 'Giáo Trình Phát Triển Hán Ngữ Tổng Hợp Sơ Cấp 2 Tập 2 - Dành Cho Người Luyện Thi HSK - Học Kèm App Online', 'MÔ TẢ SẢN PHẨM\r\n\"Giáo Trình Phát Triển Hán Ngữ Tổng Hợp Sơ Cấp 2 Tập 2” chủ yếu dùng cho sinh viên học tiếng Trung Quốc, có thể đáp ứng được yêu cầu giảng dạy của môn học chính ở các cấp độ như sơ cấp (bao gồm mới bắt đầu).\r\n\r\nCuốn sách “Giáo Trình Phát Triển Hán Ngữ Tổng Hợp Sơ Cấp 2 Tập 2” giúp bạn:\r\n-	Nâng cao khả năng nghe tiếng Trung, vận dụng ngôn ngữ hiệu quả trong thực tế giao tiếp hàng ngày.\r\n-	Người học vừa có thể theo sát nội dung được cung cấp trong tài liệu, vừa có thể tích cực, chủ động nắm bắt, trải nghiệm và tiếp thu ứng dụng ngôn ngữ từ trong cuộc sống hàng ngày. Người học dành thời gian trải nghiệm, ghi nhớ và vận dụng những nội dung được học vào thực tế, tự tạo cảm hứng học tập cho bản thân.\r\n-	Giáo viên dựa trên nghiên cứu tài liệu, xây dựng phương hướng giảng dạy phù hợp và có thể dựa vào đó để tự bổ sung và phát triển bản thân.\r\n-	Luyện nghe và phát âm chuẩn như người bản ngữ.', 'Nội dung cuốn sách:\r\n-	“Giáo Trình Phát Triển Hán Ngữ Tổng Hợp Sơ Cấp 2 Tập 2” được biên soạn bởi các chuyên gia hàng đầu về Giáo dục Hán ngữ Quốc tế của Trường Đại học Ngôn ngữ Bắc Kinh được ứng dụng giảng dạy rộng rãi tại các trường học của Trung Quốc và hiện đang được một số trường ĐH ở Việt Nam sử dụng. \r\n-	Với những cập nhật về tư duy dạy và học tiên tiến nhất, cũng như nội dung và hình thức mang tính ứng dụng cao, phù hợp với xã hội hiện đại, “Giáo trình Phát triển Hán ngữ” giúp bạn nhanh chóng tự Phát triển tiếng Trung một cách có hệ thống và hiệu quả, để bạn có thể SỬ DỤNG GIAO TIẾP và có đủ hệ thống kiến thức và năng lực THI HSK.\r\n\r\nKhi mua sách tại MCBooks:\r\n-	Sách có kèm bài luyện nghe tích hợp trên App học online MCBooks\r\n-	Tặng ngay Bookmark để dễ dàng ghi nhớ phần đã học tới của sách.\r\n-	Tặng ngay Voucher giảm giá 5% trong vòng 5 năm khi sử dụng sách của MCBooks.\r\n-	Sách được bọc túi chống sốc và hộp giấy cẩn thận để tránh nhăn quăn sách.\r\n-	Lưu ý: Chỉ có sách chính hãng của MCBooks mới có thể quét được mã QR-code cho các file nghe đi kèm với sách.\r\n\r\nThông tin sản phẩm\r\n-	Tác giả: Đại học Ngôn ngữ Bắc Kinh\r\n-	Nhà xuất bản: NXB Hồng Đức\r\n-	Công ty phát hành: MCBooks\r\n-	Số trang: 144\r\n-	Năm xuất bản: 2019\r\n-	Hình thức: Bìa mềm', '120000', '86400', 'Giáo Trình Phát Triển Hán Ngữ Tổng Hợp Sơ Cấp 2 Tập 2 - Dành Cho Người Luyện Thi HSK - Học Kèm App Online86.jpeg', '', 1, NULL, NULL),
(19, 14, 5, 'Chinh Phục Không Gian – Hành Trình Kỳ Diệu', 'MÔ TẢ SẢN PHẨM\r\nCông ty phát hành: 1980 Books \r\nTác giả: DK\r\nNhà xuất bản: NXB Dân Trí \r\nNăm xuất bản: 2020 \r\nSố trang: 192', 'Giới thiệu sách:\r\nChinh phục không gian có lẽ là cuộc chinh phục kì diệu và vĩ đại nhất mà con người đã và đang thực hiện. Khám phá vũ trụ bao la là ước mơ từ hàng ngàn năm nhưng cho đến thế kỷ thứ 20 nó mới trở nên sôi động hơn bao giờ hết. Nga – Mỹ hai cường quốc đã tạo ra một cuộc chạy đua vào không gian hết sức ngoạn mục, kết quả là hàng loạt cột mốc đã được tạo ra, đánh dấu những bước tiến hết sức quan trọng trong hành trình chinh phục không gian của con người. \r\nNội dung sách tái hiện lại những cột mốc quan trọng trong hành trình chinh phục không gian của con người : Từ việc chế tạo những con tàu vũ trụ đầu tiên, lần đầu tiên con người bay vòng quanh Trái Đất, lần đầu tiên con người đặt chân lên Mặt Trăng, xây dựng trạm vũ trụ quốc tế, … Cùng với việc tái hiện lại hành trình chinh phục không gian cuốn sách còn đề cập đến những hiệu ứng tích cực của quá trình chinh phục không gian lên cuộc sống thường ngày trên Trái Đất cũng như những bước đi tiếp theo của con người trong hành trình chinh phục không gian tương lai. \r\nNgày nay, với sự phát triển của khoa học kỹ thuật, liệu con người sẽ hiện thực hóa ước mơ sống trên sao Hỏa? Con người sẽ phát triển ngành du lịch không gian? Con người sẽ tìm ra những nền văn minh ngoài Trái Đất? Và sẽ còn những cột mốc phi thường nào mà con người sẽ đạt được?', '200000', '160000', 'Chinh Phục Không Gian – Hành Trình Kỳ Diệu24.jpeg', '', 1, NULL, NULL),
(20, 13, 4, 'Combo 4 cuốn Giáo Trình Phát Triển Hán Ngữ Sơ Cấp 1 Nghe + Nói - Giao Tiếp + Tổng Hợp (T1 + T2)', 'GIỚI THIỆU SÁCH\r\n\r\nTrọn bộ Giáo trình Phát triển Hán Ngữ Sơ cấp 2 gồm 4 quyển:\r\n- Giáo Trình Phát Triển Hán Ngữ Nghe Sơ Cấp 2\r\n- Giáo Trình Phát Triển Hán Ngữ Nói - Giao Tiếp Sơ Cấp 2\r\n- Giáo Trình Phát Triển Hán Ngữ Tổng Hợp Sơ Cấp 2 - Tập 1\r\n- Giáo Trình Phát Triển Hán Ngữ Tổng Hợp Sơ Cấp 2 - Tập 2', 'Bộ sách giúp bạn:\r\n• Nâng cao khả năng nghe tiếng Trung, qua đó sử dụng tiếng Trung như một công cụ giao tiếp chứ không đơn thuần là học lý thuyết. Mục tiêu quan trọng nhất là vận dụng ngôn ngữ hiệu quả trong thực tế giao tiếp hàng ngày.\r\n• Người học vừa có thể theo sát nội dung được cung cấp trong tài liệu, vừa có thể tích cực, chủ động nắm bắt, trải nghiệm và tiếp thu ứng dụng ngôn ngữ từ trong cuộc sống hàng ngày. Người học dành thời gian trải nghiệm, ghi nhớ và vận dụng những nội dung được học vào thực tế, tự tạo cảm hứng học tập cho bản thân.\r\n• Giáo viên dành thời gian cho việc phân tích nghiên cứu tài liệu, xây dựng phương hướng giảng dạy phù hợp. Giáo viên vừa có thể sử dụng cuốn sách như một tài liệu hữu ích, có thể dựa vào đó để tự bổ sung và phát triển, đồng thời tạo điều kiện cho người học cùng phát triển bằng cách kết hợp tìm hiểu thêm kiến thức từ nhiều nguồn.\r\n• Học mọi lúc, mọi nơi với ứng dụng MCBooks trên điện thoại. Hơn nữa, những đoạn thoại trong cuốn sách đều có trên ứng dụng giúp bạn nghe nội dung hội thoại với giọng người bản ngữ chuẩn thay vì nghe giáo viên đọc như cách học truyền thống.\r\n-----\r\nNhà phát hành	MCBooks\r\nNhà xuất bản	NXB Hồng Đức\r\nTác giả	Đại học Ngôn ngữ Bắc Kinh\r\nNăm xuất bản	 2019\r\nLoại bìa	Bìa mềm\r\nSố trang	900/ 4 cuốn', '252000', '231000', 'Combo 4 cuốn Giáo Trình Phát Triển Hán Ngữ Sơ Cấp 1 Nghe + Nói - Giao Tiếp + Tổng Hợp (T1 + T2)97.jpeg', '', 1, NULL, NULL),
(21, 14, 5, 'Hiểu hết về khoa học - How Science Works', 'MÔ TẢ SẢN PHẨM\r\nTác giả: DK\r\nDịch giả: Nguyễn Kim Phụng\r\nNhà xuất bản: Thế Giới\r\nSố trang: 256\r\nKích thước: 20.5 x 23 cm\r\nNgày phát hành: 06-2021', 'HOW SCIENCE WORKS – HIỂU HẾT VỀ KHOA HỌC\r\nCUỐN SÁCH NHẬP MÔN KHOA HỌC ĐƠN GIẢN NHẤT, TRỰC QUAN NHẤT – TỪ TRƯỚC TỚI NAY!\r\n\r\nTrí tuệ nhân tạo có sắp thống trị thế giới? Liệu pháp gien hoạt động như thế nào? Tế bào gốc, hạt Higgs, hay sóng hấp dẫn là gì?\r\n\r\nKhoa học có thể phức tạp đến độ làm bạn nản chí, và có những lúc không dễ để tách bạch khoa học “đúng” và ngụy khoa học. Để có được câu trả lời đơn giản, súc tích và thỏa đáng cho những thắc mắc của mình trong các lĩnh vực khoa học tự nhiên từ vật lý, hóa học, sinh học đến khoa học về Trái Đất và vũ trụ, mời bạn đến với How Science Works – Hiểu hết về khoa học.\r\n\r\nVới lối giải thích dễ hiểu, cách trình bày trực quan, minh họa màu đẹp mắt, cuốn sách này sẽ giúp bạn nắm bắt được hàng trăm điều lý thú mà khái niệm “khoa học” tưởng như phức tạp còn đang cất giấu. Cánh cửa dẫn lối đến khoa học – từ những khái niệm cơ bản nhất cho đến những công nghệ tiên tiến nhất – đang ở ngay đây chờ bạn mở ra!\r\n\r\n#sách #sach #sachnhanam\r\n#DK #Howscienceworks #Hieuhetvekhoahoc', '264000', '240500', 'Hiểu hết về khoa học - How Science Works63.jpeg', '', 1, NULL, NULL),
(22, 14, 5, 'Hiểu hết về tâm lý học - How Psychology Works', 'MÔ TẢ SẢN PHẨM\r\nTác giả: DK\r\nCố vấn biên tập: Jo Hemmings\r\nDịch giả: Trần Trương Phúc Hạnh , Phương Hoài Nga hiệu đính\r\nNhà xuất bản: Thế Giới\r\nSố trang: 247\r\nKích thước: 19.5x23 cm\r\nNgày phát hành: 11-2020', 'MỘT TRONG NHỮNG CUỐN SÁCH MỞ KHÓA HỮU ÍCH NHẤT VỀ TƯ DUY, KÝ ỨC VÀ CẢM XÚC CỦA CON NGƯỜI!\r\n\r\nÁm sợ là gì, ám sợ có thực sự đáng sợ không? Rối loạn tâm lý là gì, làm thế nào để thoát khỏi tình trạng suy nhược và xáo trộn đó? Trầm cảm là gì, vì sao con người hiện đại thường xuyên gặp và chống chọi với tình trạng u uất, mệt mỏi và tuyệt vọng này?\r\n\r\nTìm hiểu về các vấn đề tâm trí của con người luôn đầy sức hấp dẫn và lôi cuốn, vì vậy mà tâm lý học ra đời, hình thành và phát triển rất nhiều các học thuyết và trường phái. Cuốn sách này dẫn dắt bạn đọc qua hành trình tìm hiểu các học thuyết và trường phái đó, về cách các nhà tâm lý diễn giải hành xử và tâm trí con người. Tại sao chúng ta có những hành vi, suy nghĩ và cảm xúc như vậy, chúng diễn ra và kết thúc như thế nào, chúng ảnh hưởng lâu dài, gián đoạn hay ngắn ngủỉ đến đời sống của chúng ta ra sao, làm thế nào để chúng ta thoát khỏi những tác động tiêu cực của chúng?\r\n\r\nCuốn sách có cấu trúc khoa học, trình bày dễ hiểu, súc tích, thiết kế và minh họa đẹp mắt này sẽ mang đến cho bạn những hiểu biết về các học thuyết tâm lý và các phương pháp trị liệu, từ các vấn đề cá nhân đến những ứng dụng thực tế.', '240000', '115000', 'Sách - Hiểu hết về tâm lý học - How Psychology Works48.jpeg', '', 1, NULL, NULL),
(23, 14, 5, 'Botanicum - Welcom to the Museum - Tìm hiểu về thực vật', 'MÔ TẢ SẢN PHẨM\r\nSách : Botanicum - Welcom to the Museum - Tìm hiểu về thực vật', 'Phiên bản mini trang nhã của cuốn sách bán chạy nhất Animalium này là món quà hoàn hảo cho những người yêu thực vật hoặc bất kỳ ai đánh giá cao những cuốn sách đẹp. Đi lang thang trong các phòng trưng bày - mở cửa 365 ngày một năm - và khám phá bộ sưu tập các cuộc triển lãm được tuyển chọn trên mỗi trang, kèm theo văn bản thông tin. Mỗi chương có một nhánh khác nhau của cây sự sống. Botanicum như một món quà tuyệt vời làm ta liên tưởng ngay tới Vườn bách thảo Hoàng gia tại Kew. ĐẦY ĐỦ - ĐỒ SỘ với hơn 160 loại thực vật từ nhỏ tới lớn: tảo, bắp cải, cà rốt, bí ngô … đều được phác họa dưới ngòi bút thần thánh của Katie Scott. Chào mừng đến với bảo tàng!\r\n\r\nThuộc tính sản phẩm:\r\ntác giả : Jenny Broom \r\nNhà Xuất Bản : Parragon \r\nCông ty Phát Hành : Á Châu Education\r\nCho các lứa tuổi 12-17\r\nđịnh dạng Bìa cứng \r\nsố trang  64 trang\r\nKích thước 130 x 175 x 10mm \r\n trọng lượng 500g\r\nNgày xuất bản 12 thg 9, 2018', '220000', '200240', 'Welcom to the Museum - Tìm hiểu về thực vật95.jpeg', '', 1, NULL, NULL),
(24, 15, 5, 'Lịch sử khoa học - Bách khoa thư các khám phá thay đổi thế giới', 'MÔ TẢ SẢN PHẨM\r\nCông ty phát hành:Đông A\r\nTác giả:DK\r\nNXB:NXB Dân Trí\r\nNgày phát hành:2020\r\nLoại bìa:Bìa cứng\r\nSố trang:400\r\nKích thước:30*25\r\nVới nhiều hình ảnh sinh động và thông tin lí thú, Lịch sử Khoa học là hành trình hấp dẫn khám phá những phát kiến và phát minh khoa học quan trọng. Từ việc sử dụng lửa cho đến phát minh ra penicillin và sự khởi đầu của mạng internet toàn cầu, niềm khao khát tìm hiểu và cải biến thế giới của loài người chưa bao giờ nguôi ngoai. Nhìn lại những di sản khoa học và công nghệ của nhân loại sẽ giúp ta hiểu được những tư tưởng đã hình thành nên quá khứ, hiện tại và có thể sẽ định hình tương lai.', 'DÒNG THỜI GIAN TRỰC QUAN\r\n\r\nDòng thời gian ba tầng sẽ dẫn người đọc đi qua những sự kiện quan trọng nhất trong lịch sử khoa học. Phần văn bản thuật lại những phát kiến chính yếu, kể câu chuyện về các thí nghiệm và cải tiến kĩ thuật đã góp phần định nghĩa thế giới chúng ta ngày nay cũng như chân dung của những con người đứng sau những kì công ấy. Các sự kiện chính sẽ được giải thích kèm những hình ảnh minh họa trực quan, sơ đồ chú thích cụ thể và những trích dẫn nổi tiếng. Từ thời tiền sử, khám phá ra lửa, đồ gốm, bánh xe, qua Trung Quốc và thế giới Hồi giáo thời cổ đại, đến cuộc cách mạng khoa học của châu Âu và kỉ nguyên công nghiệp, Lịch sử Khoa học sẽ kết thúc bằng những bước phát triển nhanh chóng của thế kỉ 21.\r\n\r\nKHÁM PHÁ VÀ GIẢI THÍCH\r\n\r\nCách trình bày đặc trưng giúp giới thiệu chi tiết về các khía cạnh của khoa học, từ những dụng cụ được dùng để quan sát và đo đạc thế giới xung quanh, cho đến việc lần theo sự phát triển của kĩ thuật, như chiếc bánh xe, động cơ, rồi đến thời kì robot. Các hình ảnh vi tính sẽ minh họa rõ nét hơn những nguyên lí và phát kiến khoa học, đồng thời giải thích rõ ràng hơn cách vận hành của chúng.\r\n\r\nBao hàm mọi khía cạnh, Lịch sử Khoa học mang luồng sinh khí mới của lịch sử các phát kiến đến mọi lứa tuổi độc giả.', '220000', '127440', 'Lịch sử khoa học - Bách khoa thư các khám phá thay đổi thế54.jpeg', '', 1, NULL, NULL),
(25, 15, 5, 'Lịch Sử Quân Sự - Bách Khoa Thư Các Loại Vũ Khí Định Hình Thế Giới', 'MÔ TẢ SẢN PHẨM\r\nCông ty phát hành: Đông A \r\nTác giả: DK \r\nNXB: NXB Dân trí \r\nNgày phát hành: 2019 \r\nSố trang: 448\r\nLoại bìa: Bìa cứng \r\nKích thước: 25*30', 'Tập hợp đầy đủ các loại binh khí, máy móc, vật dụng quân sự, từ rìu tay đá lửa thời tiền sử tới vệ tinh do thám và máy bay không người lái ngày nay, Lịch sử Quân sự là bách khoa thư toàn diện về lịch sử không ngừng biến thiên của công nghệ chiến tranh.\r\n\r\nNhững minh họa chi tiết, sống động sẽ giúp độc giả như được tham quan tận mắt những đặc tính quan trọng của hơn 1750 loại vũ khí, giáp trụ, xe cộ và công sự. Tất cả được đặt trong các diễn biến chính được tường thuật rõ ràng của lịch sử chiến tranh, cùng bối cảnh chính trị - xã hội rộng lớn hơn. Kèm theo là những trận đánh nổi tiếng được phân tích trên phương diện vũ khí và chiến lược, nơi những tiến bộ công nghệ quân sự nổi bật trên tấm bản đồ quyền lực đầy biến động của các đế chế.\r\n\r\nLịch sử Quân sự, thành quả chung của nhóm chuyên gia đến từ Bảo tàng Vũ khí Hoàng gia Anh, Viện Smithsonian và các bảo tàng hàng đầu khác, sẽ cung cấp cho bạn đọc một bản tường thuật lý thú về những loại vũ khí và chiến thuật đã làm thay đổi bản chất chiến tranh.', '440000', '367000', 'Lịch Sử Quân Sự - Bách Khoa Thư Các Loại Vũ Khí Định Hình Thế Giới58.jpeg', '', 1, NULL, NULL),
(26, 15, 5, 'Lịch Sử Thế Giới: Chân Dung Nhân Loại Theo Dòng Sự Kiện', 'MÔ TẢ SẢN PHẨM\r\nCông ty phát hành: Công Ty Cổ Phần Văn Hóa Đông A\r\nTác giả: DK & Viện Smithsonian (biên soạn)\r\nNgày xuất bản: 02-2017\r\nKích thước: 25 x 30 cm\r\nNhà xuất bản: Nhà Xuất Bản Dân Trí\r\nLoại bìa: Bìa cứng\r\nSố trang: 512', 'Lịch Sử Thế Giới: Chân Dung Nhân Loại Theo Dòng Sự Kiện\r\n\r\nVới lượng thông tin và hình ảnh đồ sộ, cuốn sách là hành trình hấp dẫn giúp bạn khám phá các bước tiến lịch sử đã làm nên chân dung nhân loại ngày nay.\r\n\r\n\r\nTOÀN DIỆN VÀ ĐỘC ĐÁO\r\n\r\nMở đầu từ sự xuất hiện loài người, cuốn sách đưa bạn qua tiến trình phát triển và suy tàn của các nền văn minh trên toàn thế giới, mà trọng tâm là các sự kiện chiến tranh, thương mại và thám hiểm, các cuộc cách mạng và đổi thay công nghệ… Đặc biệt, nội dung sách không chia theo chủ đề hay khu vực mà được trình bày theo diễn tiến thời gian nhằm giúp bạn có cái nhìn toàn cảnh và so sánh về sự phát triển của các quốc gia, dân tộc tại cùng một thời điểm trong quá khứ.\r\n\r\nMINH HỌA SỐNG ĐỘNG\r\n\r\nBên cạnh lượng thông tin dồi dào, cuốn sách còn cung cấp hơn 1000 hình minh họa độc đáo bao gồm các hiện vật, tác phẩm nghệ thuật, ảnh tư liệu nhằm tái hiện chân dung các nền văn hóa, sự kiện và nhân vật. Ngoài ra, cuốn sách còn có hàng trăm biểu đồ, bản đồ và số liệu phản ánh quy mô, tính chất và diễn biến của các sự kiện, thời kì lịch sử.\r\n\r\nĐỘI NGŨ BIÊN SOẠN UY TÍN\r\n\r\nĐây là công trình biên soạn của nhiều giảng viên, nhà nghiên cứu từ các trường đại học danh tiếng của Anh quốc như Oxford, Cambridge, Edinburgh cùng sự đóng góp của đội ngũ chuyên gia từ Viện Smithsonian, một hệ thống bao gồm gần 20 bảo tàng và trung tâm nghiên cứu cấp quốc gia của Hoa Kỳ, nơi lưu giữ 138 triệu hiện vật cùng các tư liệu quý giá về lịch sử nhân loại.\r\n\r\nĐẶC BIỆT BỔ SUNG\r\n\r\nVới mong muốn tiếp cận tốt hơn sự quan tâm của độc giả trong nước, nhóm biên dịch và hiệu đính đã bổ sung một số sự kiện lịch sử tiêu biểu của Việt Nam vào ấn bản tiếng Việt. Các sự kiện này được chọn lọc một cách cẩn trọng, tuân thủ tinh thần chung của bản gốc để đảm bảo tính nhất quán với tổng thể. Qua đó, bạn đọc sẽ có cái nhìn đối sánh về trình độ phát triển, các bước tiến của lịch sử Việt Nam so với các quốc gia trong khu vực và trên thế giới.', '472000', '410000', 'Lịch Sử Thế Giới Chân Dung Nhân Loại Theo Dòng Sự Kiện56.jpeg', '', 1, NULL, NULL),
(27, 15, 5, 'Những Nhà Khoa Học Thay Đổi Lịch Sử', 'MÔ TẢ SẢN PHẨM\r\nNhững nhà khoa học thay đổi lịch sử\r\nTác giả: DK\r\nNgười dịch: Nguyễn Minh\r\nKích thước: 19,3 cm x 23,5 cm\r\nSố trang: 320\r\nGiá bìa: 390.000đ\r\nHình thức bìa: Bìa cứng, có áo, in màu toàn bộ\r\nMã sản phẩm: 8936071678126\r\nMã ISBN: 978-604-304-811-7', 'Sách do Công ty Cổ phần Văn hóa Đông A và NXB Dân trí liên kết ấn hành\r\nTrải suốt tiến trình lịch sử, con người luôn bị thôi thúc, muốn hiểu rõ về thế giới xung quanh mình. Khoa học là nỗ lực đã và vẫn đang tiếp diễn của nhân loại nhằm tìm hiểu cách thức vũ trụ vận hành. Trên hành trình không ngừng nghỉ đó, có những cá nhân kiệt xuất đã để lại thành tựu vẫn còn ghi dấu trong cuộc sống ngày nay. Những thành tựu đó đồng thời trở thành động lực cho các thế hệ mai sau nỗ lực khám phá vũ trụ vô tận.\r\n\r\nNhững nhà khoa học thay đổi lịch sử là tập hợp các thông tin về cuộc đời, sự nghiệp, thành tựu của 86 nhà khoa học xuất chúng trong lịch sử phát triển của loài người: Từ những triết gia Hy Lạp với phương pháp triết lý như Thales xứ Miletus (thế kỷ VI TCN), Aristotle,  đến những học giả phương Đông như al-Khwarizmi, Alhazen; từ Copernicus, Galileo Galilei với thuyết “nhật tâm” mở màn cho quan niệm hiện đại về vũ trụ đến định luật vạn vật hấp dẫn của Newton, phát minh động cơ điện của Michael Faraday, Alexander Fleming với phát hiện về penicillin, cứu sống ít nhất 200 triệu nhân mạng trong Thế chiến II, thuyết tương đối của Albert Einstein, hay Alan Turing với máy tính, và mạng lưới toàn cầu World Wide Web của Tim Berners-Lee …\r\n\r\nHọ là những nhân vật vĩ đại, với những khám phá, phát kiến thay đổi lịch sử.\r\n\r\nCác nhân vật được giới thiệu theo dòng thời gian, với nhiều minh họa sinh động, không chỉ giúp bạn đọc tìm hiểu sự phát triển của khoa học qua các thời kỳ mà còn kể câu chuyện về cuộc đời của những thiên tài trong lịch sử.', '292500', '260000', 'Những Nhà Khoa Học Thay Đổi Lịch Sử21.jpeg', '', 1, NULL, NULL),
(28, 15, 5, 'Những Nhà Lãnh Đạo Thay Đổi Lịch Sử', 'MÔ TẢ SẢN PHẨM\r\nNhững nhà lãnh đạo thay đổi lịch sử\r\nTác giả: DK\r\nNgười dịch: Kim Oanh\r\nKích thước: 19.3 cm x 23.5 cm\r\nSố trang: 320\r\nGiá bìa: 390.000đ\r\nHình thức bìa: Bìa cứng, có áo\r\nMã sản phẩm: 8936071678133\r\nMã ISBN: 978-604-304-810-0', 'Sách do Công ty Cổ phần Văn hóa Đông A và NXB Dân trí liên kết ấn hành\r\nTrong suốt chiều dài lịch sử, xã hội loài người đã được định hình và định đoạt bởi những người lãnh đạo xã hội. Cho dù giành thắng lợi bằng cách chinh phục hoặc sử dụng thủ đoạn chính trị, hay thành công bằng niềm tin vào bản thân, họ là những tinh hoa quyền lực mà người khác mong muốn tuân theo, hoặc bị bắt buộc phải tuân theo.\r\n\r\nNhững nhà lãnh đạo thay đổi lịch sử là tập hợp các thông tin về cuộc đời, thân thế và ảnh hưởng… của 81 nhà lãnh đạo đã từng xuất hiện trong lịch sử, trên nhiều lĩnh vực: Từ bậc quân vương và nhà độc tài như Alexander Đại đế, Suleiman Uy Vũ hay Adolf Hitler; nhà cải cách xã hội và nhà cách mạng như Martin Luther King Jr, Nelson Mandela, Mohandas Gandhi hay Karl Marx; cho đến nhà tiên tri và người hướng dẫn tinh thần như Moses, Đức Phật Siddhartha Gautama, Jesus Christ hay Muhammad…\r\n\r\nPhong cách lãnh đạo của họ cũng rất đa dạng: một số người đạt được và duy trì quyền lực qua nỗi sợ hãi và sự tàn bạo, trong khi những người khác truyền cảm hứng cho lòng trung thành bằng sự nhân từ hoặc lý tưởng và khát vọng chung. Trong khi di sản của nhiều nhà lãnh đạo đã phai nhạt dần theo thời gian, một số ít những nhà lãnh đạo kiệt xuất đã để lại dấu ấn không thể xóa mờ trên thế giới, hoặc bất tử về mặt tinh thần như những biểu tượng dân tộc được trân trọng.\r\n\r\nMột đặc trưng nhất quán của tất cả các nhà lãnh đạo xuất hiện trong cuốn sách này, đó là tham vọng và khả năng gây ảnh hưởng. Họ đã đón nhận, đấu tranh cho vị trí của mình và không ngại đứng lên vì những gì họ tin tưởng, dù điều đó mang lại lợi ích hay gây ra tổn hại cho nhân loại. Đây là những người sống cuộc đời khác thường, nhiều người trong số họ vẫn tiếp tục gây ảnh hưởng đến cuộc sống ngày nay.', '292500', '246880', 'Những Nhà Lãnh Đạo Thay Đổi Lịch Sử59.jpeg', '', 1, NULL, NULL),
(29, 16, 5, 'Chính trị - Khái lược những tư tưởng lớn', 'MÔ TẢ SẢN PHẨM\r\nTác giả: DK\r\nDịch giả: Bích Thu dịch, Lê Ngọc Tân hiệu đính\r\nNăm xuất bản: 2019\r\nNhà xuất bản Dân Trí\r\nSố trang: 352\r\nKích thước: 19,3 x 23,5 cm\r\nTrọng lượng: 700gr', 'Có đúng chăng khi chúng ta lật đổ một nhà cai trị bất công? Liệu nền dân chủ có thực sự là hình\r\nthức chính quyền tốt nhất? Và chiến tranh có thể được biện minh hay không? Xuyên suốt chiều\r\ndài lịch sử, loài người đã tự hỏi mình những điều này cùng những câu hỏi lớn lao khác về cách\r\nthức tốt nhất để chúng ta cai trị chính mình – và các tư tưởng gia vĩ đại đã đưa ra những lời giải\r\nđáp mà cho đến nay vẫn đang tiếp tục định hình thế giới.\r\nVới văn phong dễ hiểu và sáng sủa, Chính trị - Khái lược những tư tưởng lớn là tập hợp những\r\nbài viết ngắn gọn hàm súc giải thích rõ những điều khó hiểu, những sơ đồ từng bước giúp làm\r\nsáng tỏ những lí thuyết rối rắm, và những hình ảnh minh họa dí dỏm giúp chúng ta ý thức rõ hơn\r\nvề vai trò của mình trong cách thức tổ chức xã hội.\r\n\r\n#sachdonga #donga #khailuoc #khailuocnhungtutuonglon #khailuoctutuong #chinhtri #tongiao #tamlyhoc #kinhdoanh #triethoc #kinhtehoc', '304000', '299999', 'Chính trị - Khái lược những tư tưởng lớn20.jpeg', '', 1, NULL, NULL),
(30, 16, 5, 'Triết học - Khái lược những tư tưởng lớn', 'MÔ TẢ SẢN PHẨM\r\nCông ty phát hành:Đông A\r\nTác giả:DK\r\nNXB:NXB Dân Trí\r\nNgày phát hành:2019\r\nLoại bìa:Bìa cứng\r\nSố trang:352\r\nKích thước:19.3*23.5', 'Vũ trụ khởi đầu như thế nào? Chân lí là gì? Làm thế nào để ta có thể sống một cuộc đời tốt đẹp? Xuyên suốt chiều dài lịch sử, loài người đã tự hỏi mình những điều này cùng những câu hỏi lớn lao khác về bản chất của đời sống và tồn tại – và các tư tưởng gia vĩ đại đã đưa ra những lời giải đáp mà cho đến nay vẫn đang tiếp tục định hình thế giới của chúng ta.\r\n\r\nVới văn phong dễ hiểu và sáng sủa, Triết học – Khái lược những tư tưởng lớn là tập hợp những bài viết ngắn gọn hàm súc giải thích rõ những điều khó hiểu, những sơ đồ từng bước một giúp làm sáng tỏ những lí thuyết rối rắm, những trích dẫn kinh điển giúp triết học trở nên dễ nhớ, và những hình ảnh minh họa dí dỏm giúp chúng ta chơi đùa với những tư tưởng của chính mình.', '312000', '302000', 'Triết học - Khái lược những tư tưởng lớn72.jpeg', '', 1, NULL, NULL),
(31, 17, 5, 'The Story Of Food - Câu Chuyện Thực Phẩm', 'MÔ TẢ SẢN PHẨM', 'GIỚI THIỆU SÁCH\r\n\r\nThe Story Of Food - Câu Chuyện Thực Phẩm\r\n\r\nTừ loài cá khơi nguồn cho cả một cuộc chiến tranh cho đến loại rau gia vị từng được coi là biểu tượng của sự căm ghét để rồi nhiều thế kỷ sau lại trở thành biểu tượng cho tình yêu, thực phẩm đã luôn là một phần không thể thiếu của văn hóa nhân loại. Những câu chuyện về nguồn gốc, truyền thống nuôi trồng và nấu nướng của gần hai trăm loại thực phẩm được truyền tải sống động qua những trang sách minh họa của The Story of Food – Câu chuyện thực phẩm sẽ mang lại một bữa đại tiệc bổ ích cho những người say mê ẩm thực ở khắp mọi nơi.\r\n\r\nHơn cả những mẩu thông tin đơn lẻ về từng loại thực phẩm, cuốn sách xâu chuỗi chúng thành từng nhóm thực phẩm, khiến thông tin biến thành một câu chuyện (có minh họa) dễ theo dõi, có diễn biến qua thời gian và vùng miền.\r\n\r\n---------------------\r\nTên Nhà Cung Cấp	Nhã Nam\r\nTác Giả  DK\r\nDịch Giả	Trần Trương Phúc Hạnh \r\nNăm XB	02-2021\r\nSố trang	362\r\nNXB NXB Thế Giới\r\nHình Thức Bìa mềm', '374220', '342000', 'The story of food - Câu chuyện thực phẩm71.jpeg', '', 1, NULL, NULL),
(32, 18, 5, '1000 Từ Tiếng Anh Hữu Ích (Song Ngữ Việt - Anh) - Xây Dựng Vốn Từ Vựng Và Kỹ Năng Ngôn Ngữ Cho Trẻ', 'MÔ TẢ SẢN PHẨM\r\nCông ty phát hành: Đông A\r\nNhà xuất bản: Dân Trí\r\nTác giả: DK\r\nDịch giả: Thùy Dương\r\nKích thước: 21.6 x 27.6 cm\r\nNăm xuất bản: 2020\r\nSố trang: 61 trang\r\nHình thức: Bìa cứng có bìa áo metalize, in màu toàn bộ', 'Cuốn 1000 từ tiếng Anh hữu ích dành cho các bé bắt đầu làm quen với tiếng Anh. Mỗi trang đều được minh họa đầy màu sắc để phụ huynh và bé cùng đọc sách thật vui, đồng thời giúp bé phát triển hệ từ vựng và kỹ năng ngôn ngữ. Với các từ vựng được chọn lọc kỹ càng và tham khảo ý kiến của chuyên gia tư vấn giáo dục hàng đầu, cuốn sách này sẽ giúp trẻ mở rộng vốn từ cũng như bồi đắp cho trẻ khả năng đọc và viết từ sớm. Các từ vựng trong sách được sắp xếp theo những chủ đề thường nhật, liên hệ với đời sống mà trẻ đã quen thuộc, từ động vật hoang dã, nghề nghiệp đến màu sắc, hình dạng và con số…\r\n\r\nSách cũng bao gồm năm truyện tranh đơn giản được minh họa ngộ nghĩnh, đáng yêu. Các câu chuyện này giới thiệu những từ thông dụng, đặt từ vựng vào trong ngữ cảnh để giúp bé phát triển kỹ năng đặt câu cũng như kỹ năng kể chuyện.\r\n\r\nSở hữu cuốn sách tuyệt vời này, các bạn nhỏ sẽ tha hồ vừa học những từ vựng bổ ích vừa thư giãn với những tranh vẽ và ảnh chụp đầy sống động cũng như các trò chơi tương tác thú vị.\r\n\r\nVề Dorling Kindersley:\r\n\r\nDorling Kindersley (hay DK) là nhà xuất bản uy tín hàng đầu, từng đạt nhiều giải thưởng ở mảng sách hình ảnh dành cho thiếu nhi, được các bậc phụ huynh cũng như các thầy cô tin tưởng và được trẻ em trên toàn thế giới yêu mến. Các sách của DK luôn kết hợp tài tình những hình ảnh minh họa đặc sắc với nội dung được biên soạn mạch lạc, rõ ràng bởi các chuyên gia trong từng lĩnh vực.', '112500', '102000', '1000 Từ Tiếng Anh Hữu Ích (Song Ngữ Việt - Anh) - Xây Dựng Vốn Từ Vựng Và Kỹ Năng Ngôn Ngữ Cho Trẻ57.jpeg', '', 1, NULL, NULL),
(33, 18, 5, 'Tri Thức Về Vạn Vật - Một Thế Giới Trực Quan Chưa Từng Thấy', 'MÔ TẢ SẢN PHẨM', 'Cuốn sách được làm theo phiên bản cập nhật mới nhất của DK năm 2018.\r\n\r\nCuốn sách này sẽ cho bạn thấy những điều bách khoa thư thông thường chỉ nói.\r\n\r\nKHÁM PHÁ bí ẩn lăng mộ vua Tutankhamun, sức mạnh khủng khiếp của sóng thần, động đất, vòi rồng và lò phản ứng hạt nhân bên trong Mặt trời.\r\n\r\nNHÌN THẤY sự hình thành một ngôi sao, bên trong cơ thể khủng long bạo chúa và bí mật ADN.\r\n\r\nDU HÀNH ngược thời gian về thời xây Vạn Lý Trường Thành, đền Pantheon của Athens cổ đại và hai cuộc chiến tranh thế giới tàn khốc.\r\n\r\nKHẢO SÁT tín hiệu từ không gian, lực từ trường vô hình và tri thức khoa học ẩn giấu trong pháo hoa.\r\n\r\nSử dụng các tác phẩm đồ họa máy tính ngoạn mục, Tri thức về vạn vật sẽ hé lộ những chi tiết kỳ diệu chưa từng thấy về vũ trụ, Trái đất, thiên nhiên, cơ thể người, khoa học và lịch sử. Trọn vẹn những sự thật kinh ngạc, dòng thời gian sinh động và hình ảnh ấn tượng, cuốn bách khoa thư gia đình đầy hấp dẫn này sẽ biến những chủ đề phức tạp nhất hóa dễ dàng chỉ trong nháy mắt.   \r\n\r\nĐược thành lập năm 1974, DK nổi tiếng với dòng sách mình họa tranh kèm nội dung chú thích bóng bẩy hấp dẫn người đọc.\r\n\r\nDK được bảo trợ bởi rất nhiều các tổ chức hàn lâm cũng như nhân đạo nổi tiếng và danh giá, như Hiệp hội Y khoa Vương quốc Anh, Hội Làm vườn Hoàng gia và Tổ chức Chữ thập Đỏ Anh.\r\n\r\nNgoài sách truyền thống, DK còn sở hữu một lượng lớn các phần mềm giáo dục và sách điện tử tương tác có nhãn hiệu DK Online (tiền thân là DK Multimedia, 1990). Vào năm 2010, DK thành lập một chợ phần mềm điện thoại nhằm đưa các sản phẩm sách của hãng đến với đông đảo độc giả sử dụng thiết bị thông mình.\r\n\r\nHiện DK đang sở hữu 411 đầu sách, 30 giải thưởng các loại cho các sách. Những đầu sách cực kỳ nổi tiếng như The Way Things Work (Vạn vật Vận hành Như thế nào?) và The Way We Work của tác giả David Macaulay đều được đón nhận rất nồng nhiệt và được các độc giả trên thế giới tìm đọc từ trước tới nay.\r\n\r\nCông ty phát hành	Alphabooks\r\nTác giả DK\r\nNgày xuất bản	05-2019\r\nKích thước	\r\n25,2 x 30,1cm\r\nLoại bìa	Bìa cứng\r\nSố trang	362\r\nSKU	8935251408089\r\n#Nhà_sách_Alpha_Books\r\n#OmegaPlus_Books\r\n#ETS_Books\r\n#Gamma_Books', '489300', '428000', 'Tri Thức Về Vạn Vật - Một Thế Giới Trực Quan Chưa Từng68.jpeg', '', 1, NULL, NULL),
(34, 19, 5, 'How Money Works - Hiểu Hết Về Tiền', 'MÔ TẢ SẢN PHẨM\r\n-------------\r\nTác giả: DK\r\nNgười dịch: Bùi Thị Quỳnh Chi\r\nNhà xuất bản: Nxb Thế giới\r\nNhà phát hành: Nhã Nam\r\nĐịnh dạng: Bìa cứng\r\nKích thước: 19.5 x 23 cm\r\nNgày phát hành: 20/08/2019\r\nSố trang: 256', 'GIỚI THIỆU\r\nHow Money Works - Hiểu Hết Về Tiền\r\n\r\nCẩm nang giới đơn giản, dễ hình dung nhất từ trước đến nay về tiền tệ và hệ thống tài chính thế giới.\r\n\r\nTiền đến từ đâu? Sự khác biệt giữa giàu có và thu nhập là gì? Các chính phủ kiểm soát tiền tệ như thế nào? Tại sao một khoản nợ lại là tốt?\r\n\r\nHow money works – Hiểu hết về tiền tìm hiểu cách thức các chính phủ kiểm soát tiền tệ, cách các công ty kiếm ra tiền, cách các thị trường tài chính vận hành, cách các cá nhân tối đa hóa thu nhập thông qua đầu tư…\r\n\r\nĐưa ra định nghĩa cho hàng trăm khái niệm, cùng với những kiến thức nền tảng nhất về các hệ thống tài chính, như trái phiếu, cổ phiếu, tiền mã hóa, bitcoin, quản lý nợ, tránh vỡ nợ trực tuyến và gọi vốn cộng đồng, cũng như cách tiền vận hành thế giới.\r\n\r\nMời bạn đón đọc.', '231000', '220000', 'How Money Works - Hiểu Hết Về Tiền38.jpeg', '', 1, NULL, NULL),
(35, 19, 5, 'How The Body Works - Hiểu Hết Về Cơ Thể', 'MÔ TẢ SẢN PHẨM\r\nTác giả: DK\r\nNgười dịch: Phạm Hằng Nguyên\r\nNhà xuất bản: Nxb Thế giới\r\nNhà phát hành: Nhã Nam\r\nĐịnh dạng: Bìa cứng\r\nKích thước: 19.5 x 23 cm\r\nNgày phát hành: 26/07/2019\r\nSố trang: 256', 'GIỚI THIỆU\r\nHow The Body Works - Hiểu Hết Về Cơ Thể\r\n\r\nTuyệt đối đừng bỏ qua cuốn sách này nếu bạn là người ham học hỏi, tìm tòi và mong muốn khám phá chính cơ thể của mình!\r\n\r\nHướng dẫn Đơn giản nhất và Trực quan nhất về Cơ Thể Người!\r\n\r\nXe máy, xe ô tô, trò chơi điện tử đều kèm theo hướng dẫn sử dụng. Cuốn sách tựa như một cuốn cẩm nang giúp bạn hiểu được chính cơ thể của bản thân mình.\r\n\r\nVới những đáp án được minh họa dễ hiểu, hấp dẫn thú vị, cuốn sách HOW THE BODY WORKS - Hiểu hết về cơ thể sẽ làm sáng tỏ quá trình phức tạp đang giữ cho chúng ta tồn tại và phát triển. \r\n\r\nMời bạn đón đọc.', '225000', '210240', 'How The Body Works  Hiểu Hết Về Cơ Thể39.jpeg', '', 1, NULL, NULL);
INSERT INTO `tbl_product` (`product_id`, `category_id`, `brand_id`, `product_name`, `product_desc`, `product_content`, `product_price`, `product_price_sale`, `product_image`, `product_stock`, `product_status`, `created_at`, `updated_at`) VALUES
(36, 19, 6, 'Tâm Lí Học - Khái Lược Những Tư Tưởng Lớn', '<p><strong>M&Ocirc; TẢ SẢN PHẨM</strong></p>\r\n\r\n<p><strong><em>C&ocirc;ng ty ph&aacute;t h&agrave;nh</em></strong>:Đ&ocirc;ng A</p>\r\n\r\n<p><strong>T&aacute;c giả</strong>:DK NXB:NXB D&acirc;n tr&iacute;</p>\r\n\r\n<p><strong>Ng&agrave;y ph&aacute;t h&agrave;nh</strong>:2019</p>\r\n\r\n<p><strong>Loại b&igrave;a</strong>:B&igrave;a cứng Số trang:352 K&iacute;ch thước:19,3*23,5</p>', '<p>Ta c&oacute; thực sự l&agrave; những c&aacute; nh&acirc;n tự do với bản sắc ri&ecirc;ng&hellip; hay tất cả ch&uacute;ng ta đều chỉ biết tu&acirc;n phục số đ&ocirc;ng?</p>\r\n\r\n<p>Thi&ecirc;n t&agrave;i l&agrave; nhờ dưỡng dục hay tự nhi&ecirc;n? V&ocirc; thức điều khiển ch&uacute;ng ta như thế n&agrave;o?</p>\r\n\r\n<p>Những c&acirc;u hỏi như tr&ecirc;n ch&iacute;nh l&agrave; tiền đề cho c&aacute;c c&ocirc;ng tr&igrave;nh của nhiều tư tưởng gia v&agrave; khoa học gia lớn của thế giới, trong một lĩnh vực gi&agrave;u sức l&ocirc;i cuốn, đ&oacute; l&agrave; t&acirc;m l&iacute; học.</p>\r\n\r\n<p>Với văn phong dễ hiểu v&agrave; s&aacute;ng sủa, T&acirc;m l&iacute; học &ndash; Kh&aacute;i lược những tư tưởng lớn l&agrave; tập hợp những b&agrave;i viết ngắn gọn h&agrave;m s&uacute;c giải th&iacute;ch r&otilde; những điều kh&oacute; hiểu, những sơ đồ gi&uacute;p l&agrave;m s&aacute;ng tỏ những l&iacute; thuyết rối rắm, những tr&iacute;ch dẫn dễ nhớ, c&ugrave;ng những minh họa d&iacute; dỏm gi&uacute;p ch&uacute;ng ta chơi đ&ugrave;a với những kiến thức nền tảng về t&acirc;m l&iacute; học.</p>', '312000', '27000', 'Tâm Lí Học - Khái Lược Những Tư Tưởng Lớn8.jpeg', '', 1, NULL, NULL),
(37, 20, 6, 'Bách khoa thiếu nhi - Trái Đất', '<p><strong>M&Ocirc; TẢ SẢN PHẨM</strong></p>\r\n\r\n<p><strong>T&aacute;c giả</strong>: DK</p>\r\n\r\n<p><strong>Gi&aacute; b&igrave;a:</strong> 100.000 ₫</p>\r\n\r\n<p><strong>NXB:</strong> NXB Phụ Nữ Việt Nam</p>\r\n\r\n<p><strong>Ph&aacute;t h&agrave;nh</strong>: Minh Thắng</p>\r\n\r\n<p><strong>Năm xuất bản</strong>: 2020</p>\r\n\r\n<p><strong>Dịch giả:</strong> L&ecirc; Hải Dạng</p>\r\n\r\n<p><strong>B&igrave;a:</strong> b&igrave;a cứng</p>\r\n\r\n<p><strong>Số trang:</strong> 160 trang</p>\r\n\r\n<p><strong>K&iacute;ch thước:</strong> 13,5 x 16,5cm</p>', '<p>------------------Giới thiệu s&aacute;ch-------------</p>\r\n\r\n<p>B&aacute;ch khoa thiếu nhi - Tr&aacute;i Đất (b&igrave;a cứng) Tr&aacute;i Đất, h&agrave;nh tinh xanh, ng&ograve;i nh&agrave; của những sa mạc kh&ocirc; cằn, những đ&igrave;nh n&uacute;i chọc trời, những đại dương s&acirc;u thẳm v&agrave; những rừng rậm m&ecirc;nh m&ocirc;ng. H&atilde;y c&ugrave;ng kh&aacute;m ph&aacute; v&eacute; Tr&aacute;i Đất - c&ugrave;ng t&igrave;m hiểu nguy&ecirc;n nh&acirc;n của động đất; t&igrave;m xem d&ograve;ng s&ograve;ng băng rộng nhất nằm ở đ&acirc;u hay tại sao kh&iacute; hậu Tr&aacute;i Đất lại đang thay đổi - trong cuốn B&aacute;ch khoa thiếu nhi th&uacute; vị n&agrave;y. Hơn 180 điều th&uacute; vị về Tr&aacute;i Đất như n&uacute;i lửa, s&ocirc;ng ng&ograve;i v&agrave; thời tiết H&igrave;nh &agrave;nh minh họa sắc n&eacute;t, độ ph&acirc;n giải cao C&aacute;c kiến thức được minh họa bằng ảnh m&ocirc; h&igrave;nh khoa học Những tri thức, mốc lịch sử bất ngờ, th&uacute; vị v&agrave; đ&aacute;ng quan t&acirc;m C&ugrave;ng t&igrave;m đọc trong bộ B&aacute;ch khoa thiếu nhi: Ai Cập cồ đại * Động vật * Cơ thể người * Động vật c&oacute; v&uacute; * Khoa học * Vũ trụ * Tr&aacute;i đất</p>', '74000', '50000', 'Bách khoa thiếu nhi - Trái Đất96.jpeg', '', 1, NULL, NULL),
(38, 21, 6, 'Hiểu hết về kinh doanh - How business works', '<p><strong>M&Ocirc; TẢ SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Nh&agrave; xuất bản:</strong> Thế Giới</p>\r\n\r\n<p><strong>Số trang:</strong> 351</p>\r\n\r\n<p><strong>K&iacute;ch thước: </strong>19.5x23 cm</p>\r\n\r\n<p><strong>Ng&agrave;y ph&aacute;t h&agrave;nh:</strong> 08-01-2020</p>', '<p>Một trong những cuốn cẩm nang về Kinh doanh dễ h&igrave;nh dung nhất từ trước tới nay! Tại sao d&ograve;ng tiền lại quan trọng? Sản xuất tinh gọn l&agrave; g&igrave;? Marketing kỹ thuật số hoạt động ra sao? Th&agrave;nh vi&ecirc;n hội đồng quản trị gồm những ai? Mua b&aacute;n doanh nghiệp l&agrave; g&igrave;? Cấu tr&uacute;c doanh nghiệp ra sao? Khấu hao l&agrave; g&igrave;? Tr&aacute;ch nhiệm x&atilde; hội của doanh nghiệp ra sao? L&agrave;m thế n&agrave;o để c&oacute; thể th&agrave;nh c&ocirc;ng trong thị trường to&agrave;n cầu của thế kỷ 21? Với những h&igrave;nh ảnh bắt mắt, đơn giản, kh&ocirc;ng sử dụng thuật ngữ, How business works - Hiểu hết về kinh doanh giải th&iacute;ch mọi kh&aacute;i niệm li&ecirc;n quan đến lĩnh vực kinh doanh, biến những kh&aacute;i niệm kh&oacute; hiểu nhất trở n&ecirc;n dễ hiểu. Mọi kh&iacute;a cạnh của hoạt động kinh doanh sẽ được xem x&eacute;t, ph&acirc;n t&iacute;ch gi&uacute;p độc giả hiểu r&otilde; hơn về kinh doanh, v&agrave; c&aacute;ch c&aacute;c h&igrave;nh th&aacute;i kinh doanh định h&igrave;nh x&atilde; hội hiện đại. D&ugrave; bạn đang muốn thăng tiến trong c&ocirc;ng việc, khởi sự doanh nghiệp của ri&ecirc;ng m&igrave;nh hay chỉ đơn giản l&agrave; muốn hiểu r&otilde; hơn thế giới kinh doanh v&agrave; t&agrave;i ch&iacute;nh, How business works - Hiểu hết về kinh doanh ch&iacute;nh l&agrave; cuốn cẩm nang kh&ocirc;ng thể bỏ qua!!!</p>', '304000', '278000', 'Hiểu hết về kinh doanh - How business works32.jpeg', '', 1, NULL, NULL),
(39, 21, 6, 'Hiểu hết về tiền, cơ thể, thức ăn, tâm lý học, kinh doanh', '<p><strong>M&Ocirc; TẢ SẢN PHẨM&nbsp;</strong></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><u>How Money Works - Hiểu Hết Về Tiền</u></strong></span></p>\r\n\r\n<p><strong>T&aacute;c giả:</strong> DK</p>\r\n\r\n<p><strong>Người dịch</strong>: B&ugrave;i Thị Quỳnh Chi</p>\r\n\r\n<p><strong>Nh&agrave; xuất bản:</strong> Nxb Thế giới</p>\r\n\r\n<p><strong>Nh&agrave; ph&aacute;t h&agrave;nh:</strong> Nh&atilde; Nam</p>\r\n\r\n<p><strong>Định dạng: </strong>B&igrave;a cứng</p>\r\n\r\n<p><strong>K&iacute;ch thước:</strong> 19.5 x 23 cm</p>\r\n\r\n<p><strong>Ng&agrave;y ph&aacute;t h&agrave;nh:</strong> 20/08/2019</p>\r\n\r\n<p><strong>Số trang: </strong>256</p>\r\n\r\n<p><strong><u>&nbsp;How The Body Works - Hiểu Hết Về Cơ Thể</u></strong></p>\r\n\r\n<p><strong>T&aacute;c giả:</strong> DK</p>\r\n\r\n<p><strong>Người dịch:</strong> Phạm Hằng Nguy&ecirc;n</p>\r\n\r\n<p><strong>Nh&agrave; xuất bản:</strong> Nxb Thế giới</p>\r\n\r\n<p><strong>Nh&agrave; ph&aacute;t h&agrave;nh:</strong> Nh&atilde; Nam</p>\r\n\r\n<p><strong>Định dạng:</strong> B&igrave;a cứng</p>\r\n\r\n<p><strong>K&iacute;ch thước:</strong> 19.5 x 23 cm</p>\r\n\r\n<p><strong>Ng&agrave;y ph&aacute;t h&agrave;nh:</strong> 26/07/2019</p>\r\n\r\n<p><strong>Số trang:</strong> 256</p>\r\n\r\n<p><strong><u>&nbsp;Hiểu hết về t&acirc;m l&yacute; học</u></strong></p>\r\n\r\n<p><strong>T&aacute;c giả: </strong>DK</p>\r\n\r\n<p><strong>Nh&agrave; xuất bản:</strong> Thế Giới</p>\r\n\r\n<p><strong>Số trang:</strong> 247</p>\r\n\r\n<p><strong>K&iacute;ch thước: </strong>19.5x23 cm</p>\r\n\r\n<p><strong>Ng&agrave;y ph&aacute;t h&agrave;nh:</strong> 11-2020</p>\r\n\r\n<p><strong>Nh&agrave; ph&aacute;t h&agrave;nh :</strong>Nh&atilde; Nam</p>\r\n\r\n<p><strong><u>&nbsp;Hiểu hết về kinh doanh</u></strong></p>\r\n\r\n<p><strong>T&aacute;c giả :</strong>DK</p>\r\n\r\n<p><strong>Nh&agrave; xuất bản: </strong>Thế Giới</p>\r\n\r\n<p><strong>Số trang: </strong>351</p>\r\n\r\n<p><strong>K&iacute;ch thước:</strong> 19.5x23 cm</p>\r\n\r\n<p><strong>Ng&agrave;y ph&aacute;t h&agrave;nh:</strong> 08-01-2020</p>\r\n\r\n<p><strong>Nh&agrave; ph&aacute;t h&agrave;nh:</strong>Nh&atilde; Nam</p>', '<p>1.How Money Works - Hiểu Hết Về Tiền Cẩm nang giới đơn giản, dễ h&igrave;nh dung nhất từ trước đến nay về tiền tệ v&agrave; hệ thống t&agrave;i ch&iacute;nh thế giới. Tiền đến từ đ&acirc;u? Sự kh&aacute;c biệt giữa gi&agrave;u c&oacute; v&agrave; thu nhập l&agrave; g&igrave;? C&aacute;c ch&iacute;nh phủ kiểm so&aacute;t tiền tệ như thế n&agrave;o? Tại sao một khoản nợ lại l&agrave; tốt? How money works &ndash; Hiểu hết về tiền t&igrave;m hiểu c&aacute;ch thức c&aacute;c ch&iacute;nh phủ kiểm so&aacute;t tiền tệ, c&aacute;ch c&aacute;c c&ocirc;ng ty kiếm ra tiền, c&aacute;ch c&aacute;c thị trường t&agrave;i ch&iacute;nh vận h&agrave;nh, c&aacute;ch c&aacute;c c&aacute; nh&acirc;n tối đa h&oacute;a thu nhập th&ocirc;ng qua đầu tư&hellip; Đưa ra định nghĩa cho h&agrave;ng trăm kh&aacute;i niệm, c&ugrave;ng với những kiến thức nền tảng nhất về c&aacute;c hệ thống t&agrave;i ch&iacute;nh, như tr&aacute;i phiếu, cổ phiếu, tiền m&atilde; h&oacute;a, bitcoin, quản l&yacute; nợ, tr&aacute;nh vỡ nợ trực tuyến v&agrave; gọi vốn cộng đồng, cũng như c&aacute;ch tiền vận h&agrave;nh thế giới. Mời bạn đ&oacute;n đọc.</p>\r\n\r\n<p>2. How The Body Works - Hiểu Hết Về Cơ Thể Tuyệt đối đừng bỏ qua cuốn s&aacute;ch n&agrave;y nếu bạn l&agrave; người ham học hỏi, t&igrave;m t&ograve;i v&agrave; mong muốn kh&aacute;m ph&aacute; ch&iacute;nh cơ thể của m&igrave;nh! Hướng dẫn Đơn giản nhất v&agrave; Trực quan nhất về Cơ Thể Người! Xe m&aacute;y, xe &ocirc; t&ocirc;, tr&ograve; chơi điện tử đều k&egrave;m theo hướng dẫn sử dụng. Cuốn s&aacute;ch tựa như một cuốn cẩm nang gi&uacute;p bạn hiểu được ch&iacute;nh cơ thể của bản th&acirc;n m&igrave;nh. Với những đ&aacute;p &aacute;n được minh họa dễ hiểu, hấp dẫn th&uacute; vị, cuốn s&aacute;ch HOW THE BODY WORKS - Hiểu hết về cơ thể sẽ l&agrave;m s&aacute;ng tỏ qu&aacute; tr&igrave;nh phức tạp đang giữ cho ch&uacute;ng ta tồn tại v&agrave; ph&aacute;t triển. Mời bạn đ&oacute;n đọc.</p>\r\n\r\n<p>3. Hiểu hết về t&acirc;m l&yacute; học MỘT TRONG NHỮNG CUỐN S&Aacute;CH MỞ KH&Oacute;A HỮU &Iacute;CH NHẤT VỀ TƯ DUY, K&Yacute; ỨC V&Agrave; CẢM X&Uacute;C CỦA CON NGƯỜI! &Aacute;m sợ l&agrave; g&igrave;, &aacute;m sợ c&oacute; thực sự đ&aacute;ng sợ kh&ocirc;ng? Rối loạn t&acirc;m l&yacute; l&agrave; g&igrave;, l&agrave;m thế n&agrave;o để tho&aacute;t khỏi t&igrave;nh trạng suy nhược v&agrave; x&aacute;o trộn đ&oacute;? Trầm cảm l&agrave; g&igrave;, v&igrave; sao con người hiện đại thường xuy&ecirc;n gặp v&agrave; chống chọi với t&igrave;nh trạng u uất, mệt mỏi v&agrave; tuyệt vọng n&agrave;y? T&igrave;m hiểu về c&aacute;c vấn đề t&acirc;m tr&iacute; của con người lu&ocirc;n đầy sức hấp dẫn v&agrave; l&ocirc;i cuốn, v&igrave; vậy m&agrave; t&acirc;m l&yacute; học ra đời, h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển rất nhiều c&aacute;c học thuyết v&agrave; trường ph&aacute;i. Cuốn s&aacute;ch n&agrave;y dẫn dắt bạn đọc qua h&agrave;nh tr&igrave;nh t&igrave;m hiểu c&aacute;c học thuyết v&agrave; trường ph&aacute;i đ&oacute;, về c&aacute;ch c&aacute;c nh&agrave; t&acirc;m l&yacute; diễn giải h&agrave;nh xử v&agrave; t&acirc;m tr&iacute; con người. Tại sao ch&uacute;ng ta c&oacute; những h&agrave;nh vi, suy nghĩ v&agrave; cảm x&uacute;c như vậy, ch&uacute;ng diễn ra v&agrave; kết th&uacute;c như thế n&agrave;o, ch&uacute;ng ảnh hưởng l&acirc;u d&agrave;i, gi&aacute;n đoạn hay ngắn ngủỉ đến đời sống của ch&uacute;ng ta ra sao, l&agrave;m thế n&agrave;o để ch&uacute;ng ta tho&aacute;t khỏi những t&aacute;c động ti&ecirc;u cực của ch&uacute;ng?</p>\r\n\r\n<p>4. Hiểu hết về kinh doanh Một trong những cuốn cẩm nang về Kinh doanh dễ h&igrave;nh dung nhất từ trước tới nay! Tại sao d&ograve;ng tiền lại quan trọng? Sản xuất tinh gọn l&agrave; g&igrave;? Marketing kỹ thuật số hoạt động ra sao? Th&agrave;nh vi&ecirc;n hội đồng quản trị gồm những ai? Mua b&aacute;n doanh nghiệp l&agrave; g&igrave;? Cấu tr&uacute;c doanh nghiệp ra sao? Khấu hao l&agrave; g&igrave;? Tr&aacute;ch nhiệm x&atilde; hội của doanh nghiệp ra sao? L&agrave;m thế n&agrave;o để c&oacute; thể th&agrave;nh c&ocirc;ng trong thị trường to&agrave;n cầu của thế kỷ 21? Với những h&igrave;nh ảnh bắt mắt, đơn giản, kh&ocirc;ng sử dụng thuật ngữ, How business works - Hiểu hết về kinh doanh giải th&iacute;ch mọi kh&aacute;i niệm li&ecirc;n quan đến lĩnh vực kinh doanh, biến những kh&aacute;i niệm kh&oacute; hiểu nhất trở n&ecirc;n dễ hiểu. Mọi kh&iacute;a cạnh của hoạt động kinh doanh sẽ được xem x&eacute;t, ph&acirc;n t&iacute;ch gi&uacute;p độc giả hiểu r&otilde; hơn về kinh doanh, v&agrave; c&aacute;ch c&aacute;c h&igrave;nh th&aacute;i kinh doanh định h&igrave;nh x&atilde; hội hiện đại.</p>', '297500', '278000', 'Hiểu hết về tiền, cơ thể, thức ăn, tâm lý học, kinh doanh66.jpeg', '', 1, NULL, NULL),
(40, 21, 6, 'Kinh tế học - Khái lược những tư tưởng lớn', '<p><strong>M&Ocirc; TẢ SẢN PHẨM</strong></p>\r\n\r\n<p><strong>C&ocirc;ng ty ph&aacute;t h&agrave;nh: </strong>C&ocirc;ng ty Cổ phần Văn h&oacute;a Đ&ocirc;ng A</p>\r\n\r\n<p><strong>T&aacute;c giả: </strong>DK</p>\r\n\r\n<p><strong>Năm xuất bản: </strong>2019</p>\r\n\r\n<p><strong>Nh&agrave; xuất bản:</strong> NXB D&acirc;n Tr&iacute;</p>\r\n\r\n<p><strong>Số trang: </strong>352</p>\r\n\r\n<p><strong>K&iacute;ch thước:</strong> 19,3 x 23,5 cm</p>\r\n\r\n<p><strong>Gi&aacute; b&igrave;a: </strong>390.000đ</p>', '<p>Kinh tế học - Kh&aacute;i lược những tư tưởng lớn Điều g&igrave; xảy ra trong giai đoạn khủng hoảng kinh tế? Tiền tệ vận h&agrave;nh ra sao? V&igrave; sao ch&uacute;ng ta phải đ&oacute;ng thuế? Kinh tế học ảnh hưởng đến từng kh&iacute;a cạnh của đời sống của ch&uacute;ng ta, từ việc đi l&agrave;m đến c&aacute;ch ti&ecirc;u tiền &ndash; v&agrave; c&aacute;c &yacute; tưởng kinh tế lớn vẫn đang tiếp tục định h&igrave;nh thế giới ng&agrave;y nay. Kinh tế học - Kh&aacute;i lược những tư tưởng lớn được viết với văn phong đơn giản k&egrave;m theo c&aacute;c biểu đồ giải th&iacute;ch ngắn gọn, dễ hiểu c&aacute;c l&iacute; thuyết quan trọng. Ngo&agrave;i ra c&ograve;n c&oacute; c&aacute;c c&acirc;u tr&iacute;ch dẫn kinh điển dễ nhớ v&agrave; c&aacute;c h&igrave;nh minh họa d&iacute; dỏm, mang lại niềm hứng th&uacute; khi t&igrave;m hiểu về kinh tế học.</p>', '312000', '288000', 'Kinh tế học - Khái lược những tư tưởng lớn47.jpeg', '', 1, NULL, NULL),
(41, 22, 6, 'Nghệ Thuật - Khái Lược Những Tư Tưởng Lớn', '<p><strong>M&Ocirc; TẢ SẢN PHẨM</strong></p>\r\n\r\n<p>Nghệ Thuật - Kh&aacute;i Lược Những Tư Tưởng Lớn</p>\r\n\r\n<p><strong>T&aacute;c giả:</strong> DK Người dịch: Thanh Loan</p>\r\n\r\n<p><strong>K&iacute;ch thước:</strong> 19,3 cm x 23,5 cm</p>\r\n\r\n<p><strong>Số trang: </strong>352</p>\r\n\r\n<p><strong>Gi&aacute; b&igrave;a:</strong> 390.000đ</p>\r\n\r\n<p><strong>H&igrave;nh thức b&igrave;a:</strong> B&igrave;a cứng, c&oacute; &aacute;o, in m&agrave;u to&agrave;n bộ</p>\r\n\r\n<p><strong>M&atilde; sản phẩm:</strong> 8936203360523</p>\r\n\r\n<p><strong>M&atilde; ISBN:</strong> 978-604-314-455-0</p>', '<p>S&aacute;ch do C&ocirc;ng ty Cổ phần Văn h&oacute;a Đ&ocirc;ng A v&agrave; NXB D&acirc;n tr&iacute; li&ecirc;n kết ấn h&agrave;nh Điều g&igrave; l&agrave;m n&ecirc;n một t&aacute;c phẩm nghệ thuật? Người Hy Lạp cổ đại tạo h&igrave;nh vẻ đẹp l&yacute; tưởng như thế n&agrave;o? M&agrave;u sắc c&oacute; g&acirc;y ảnh hưởng trực tiếp đến t&acirc;m hồn kh&ocirc;ng? Cuốn s&aacute;ch n&agrave;y khảo x&eacute;t những c&acirc;u hỏi đ&oacute; v&agrave; nhiều c&acirc;u hỏi kh&aacute;c bằng c&aacute;ch kh&aacute;m ph&aacute; c&aacute;c tr&agrave;o lưu, chủ đề v&agrave; phong c&aacute;ch nghệ thuật then chốt qua hơn 200 t&aacute;c phẩm thuộc nhiều thể loại đa dạng. Bằng ng&ocirc;n từ giản dị, Nghệ thuật &ndash; Kh&aacute;i lược những tư tưởng lớn sẽ đưa bạn xuy&ecirc;n qua l&yacute; thuyết v&agrave; lịch sử nghệ thuật bằng những h&igrave;nh ảnh về c&aacute;c t&aacute;c phẩm vĩ đại nhất tr&ecirc;n thế giới c&ugrave;ng nhiều sơ đồ h&oacute;m hỉnh gi&uacute;p kh&aacute;m ph&aacute; c&aacute;c &yacute; tưởng đằng sau. Từ tượng phồn thực thời tiền sử đến sắp đặt video đương đại, đ&acirc;y l&agrave; cuốn s&aacute;ch vỡ l&ograve;ng ho&agrave;n hảo về nghệ thuật thế giới. D&ugrave; l&agrave; người học nghệ thuật hay chỉ đơn giản l&agrave; th&iacute;ch ngắm ngh&iacute;a c&aacute;c t&aacute;c phẩm, bạn cũng sẽ t&igrave;m thấy trong đ&acirc;y nhiều hiểu biết khơi gợi suy tư về những kiệt t&aacute;c được ưa th&iacute;ch từ l&acirc;u v&agrave; cả những vi&ecirc;n ngọc mới được ph&aacute;t hiện.</p>', '312000', '278000', 'Nghệ Thuật - Khái Lược Những Tư Tưởng Lớn17.jpeg', '', 1, NULL, NULL),
(42, 23, 6, 'Bách khoa thiếu nhi Vũ trụ', '<p><strong>M&Ocirc; TẢ SẢN PHẨM</strong></p>\r\n\r\n<p><strong>T&aacute;c giả: </strong>DK</p>\r\n\r\n<p><strong>Gi&aacute; b&igrave;a:</strong> 100.000 ₫</p>\r\n\r\n<p><strong>NXB:</strong> NXB Phụ Nữ Việt Nam</p>\r\n\r\n<p><strong>Ph&aacute;t h&agrave;nh:</strong> Minh Thắng</p>\r\n\r\n<p><strong>Năm xuất bản: </strong>2019 (ISBN: 9786045670743)(M&atilde; s&aacute;ch: 8935236417648)</p>\r\n\r\n<p><strong>Dịch giả: </strong>L&ecirc; Thị Thu Ngọc Dạng</p>\r\n\r\n<p><strong>b&igrave;a:</strong> b&igrave;a cứng</p>\r\n\r\n<p><strong>Số trang:</strong> 160 trang</p>\r\n\r\n<p><strong>K&iacute;ch thước: </strong>13,5 x16,5cm</p>', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ---------------Giới thiệu s&aacute;ch------------------</p>\r\n\r\n<p>S&aacute;ch bản quyền - B&aacute;ch khoa thiếu nhi vũ trụ (b&igrave;a cứng) Vũ trụ chứa vạn vật đang tồn tại như c&aacute;c h&agrave;nh tinh, mặt trăng, c&aacute;c ng&ocirc;i sao v&agrave; những dải thi&ecirc;n h&agrave;. H&atilde;y c&ugrave;ng nhau kh&aacute;m ph&aacute; về Vũ trụ - c&ugrave;ng trả lời c&acirc;u hỏi tinh v&acirc;n l&agrave; g&igrave;, tại sao lại c&oacute; nhật thực, nguyệt thực v&agrave; c&ograve;n k&iacute;nh thi&ecirc;n văn hoạt động như thế n&agrave;o - tất cả những c&acirc;u trả lời đều nằm trong cuốn B&aacute;ch khoa thiếu nhi th&uacute; vị n&agrave;y. - Hơn 170 điều th&uacute; vị về c&aacute;c h&agrave;nh tinh, v&igrave; sao, t&agrave;u vũ trụ v&agrave; những chuyến du h&agrave;nh ngo&agrave;i kh&ocirc;ng gian - H&igrave;nh ảnh minh họa sắc n&eacute;t, độ ph&acirc;n giải cao - C&aacute;c kiến thức được minh họa bằng ảnh m&ocirc; h&igrave;nh khoa học - Những tri thức khoa học, kỷ lục th&uacute; vị v&agrave; đ&aacute;ng quan t&acirc;m #sachthieunhi #khoahoc #khampha #sachthieunhihay #bachkhoa</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -------------------Ưu điểm khi mua s&aacute;ch tại shopee nh&agrave; s&aacute;ch Minh Thắng--------------&nbsp;</p>\r\n\r\n<p>Được đ&oacute;ng g&oacute;i bao nilong b&ecirc;n ngo&agrave;i tr&aacute;nh bị hỏng s&aacute;ch, vở, truyện... - Được hỗ trợ, tư vấn v&agrave; chăm s&oacute;c tất cả những s&aacute;ch, vỏ, truyện... mua b&ecirc;n gian h&agrave;ng online - Hướng dẫn chi tiết tải file nghe (đối với s&aacute;ch c&oacute; k&egrave;m file nghe s&aacute;ch, video) - C&aacute;m ơn Qu&yacute; kh&aacute;ch h&agrave;ng đ&atilde; gh&eacute; thăm v&agrave; ủng hộ nh&agrave; s&aacute;ch Minh Thắng</p>', '75000', '50000', 'Bách khoa thiếu nhi Vũ trụ3.jpeg', '', 1, NULL, NULL),
(43, 23, 6, 'DKfindout! Times Tables Poster - Tấm áp Phích Khổng Lồ Đầy Màu Sắc Củng Cố Sự Hiểu Biết Của Trẻ', '<p><strong>M&Ocirc; TẢ SẢN PHẨM</strong></p>\r\n\r\n<p><strong>- T&aacute;c Giả:</strong> Dk</p>\r\n\r\n<p>- Nh&agrave; xuất bản Dorling Kindersley Ltd</p>\r\n\r\n<p><strong>- C&ocirc;ng Ty Ph&aacute;t H&agrave;nh :</strong> &Aacute; Ch&acirc;u Education</p>\r\n\r\n<p><strong>- Ng&ocirc;n ngữ: </strong>tiếng anh</p>\r\n\r\n<p><strong>- Định Dạng</strong> B&igrave;a mềm</p>\r\n\r\n<p><strong>- K&iacute;ch Thước :</strong> 21.01 x 0.20 x 29.69 cm</p>\r\n\r\n<p><strong>- Trọng Lượng</strong> 300g</p>\r\n\r\n<p>- Ng&agrave;y xuất bản 03 thg 7, 2018</p>', '<p><strong>S&aacute;ch :</strong> DKfindout! Times Tables Poster</p>\r\n\r\n<p>- Tấm &aacute;p Ph&iacute;ch Khổng Lồ Đầy M&agrave;u Sắc Củng Cố Sự Hiểu Biết Của Trẻ - L&agrave;m cho bảng thời gian học tập trở n&ecirc;n th&uacute; vị v&agrave; hấp dẫn cho trẻ em với tấm &aacute;p ph&iacute;ch khổng lồ đầy m&agrave;u sắc n&agrave;y, ho&agrave;n hảo cho bất kỳ bức tường ph&ograve;ng ngủ hoặc lớp học n&agrave;o.</p>\r\n\r\n<p>- Tất cả c&aacute;c bảng thời gian đến 12 x 12 đều được hiển thị với c&aacute;c bức ảnh hấp dẫn để củng cố sự hiểu biết của trẻ. DKfindout! Poster Times Tables nhắc nhở trẻ những kiến ​​thức cần thiết về ph&eacute;p nh&acirc;n. H&igrave;nh vu&ocirc;ng nh&acirc;n chia cung cấp một c&aacute;ch học thay thế trong khi danh s&aacute;ch c&aacute;c bảng thời gian l&agrave; một nguồn t&agrave;i nguy&ecirc;n để tham khảo lại nhiều lần.</p>\r\n\r\n<p>- &Aacute;p ph&iacute;ch khổng lồ trực quan ngoạn mục c&oacute; k&iacute;ch thước 602x1008mm hoặc 24x40 &quot;n&agrave;y sẽ th&aacute;ch thức trẻ ph&aacute;t triển kiến ​​thức, đặt c&acirc;u hỏi v&agrave; tăng t&iacute;nh t&ograve; m&ograve; của ch&uacute;ng theo c&aacute;ch th&uacute; vị v&agrave; hấp dẫn. Ho&agrave;n hảo cho những người học nhỏ trong Giai đoạn ch&iacute;nh 1 v&agrave; 2.</p>', '99000', '79999', 'DKfindout! Times Tables Poster - Tấm áp Phích Khổng Lồ Đầy Màu Sắc Củng Cố Sự Hiểu Biết Của Trẻ53.jpeg', '', 1, NULL, NULL),
(44, 24, 5, 'Chăm Sóc Sức Khỏe - Cẩm Nang Sơ Cấp Cứu Thường Thức', 'MÔ TẢ SẢN PHẨM\r\nSách - Cẩm nang sơ cấp cứu thường thức\r\nMã hàng	8935251412796\r\nTên Nhà Cung Cấp	Alpha Books\r\nTác giả	DK\r\nNgười Dịch	Tổ chức Giáo dục Y khoa Wellbeing\r\nNXB	NXB Dân Trí\r\nNăm XB	2019\r\nTrọng lượng (gr)	300\r\nKích thước	14.8 x 21\r\nSố trang	288\r\nHình thức	Bìa Mềm\r\nCẩm Nang Sơ Cấp Cứu Thường Thức', 'Việc trang bị các kỹ năng sơ cứu cơ bản, chính xác và vận dụng kịp thời có ý nghĩa sống còn đối với nạn nhân trong đa số tình huống tai nạn.\r\n\r\nCẩm nang sơ cấp cứu thường thức là cuốn sách cung cấp những kiến thức cơ bản nhất về sơ cấp cứu. Những lời khuyên trong sách đều được các cố vấn y khoa của hội các Tổ chức sơ cứu tại Anh đưa ra dự trên những nghiên cứu cập nhật nhất, đồng thời được chuyên gia đào tạo sơ cứu minh họa, dẫn giải theo cách vừa dễ hiểu vừa dễ nhớ.\r\n\r\n* Nội dung cuốn sách được chia thành 12 chủ điểm chính, tương ứng với 12 vấn đề quan trọng trong sơ cấp cứu:\r\n\r\n-           Việc trở thành người sơ cứu\r\n\r\n-           Cách kiểm soát các sự cố/tai nạn về sức khỏe\r\n\r\n-           Cách đánh giá tình trạng nạn nhân\r\n\r\n-           Cách xử trí khi nạn nhân bất tỉnh\r\n\r\n-           Cách xử lý các vấn đề về hô hấp\r\n\r\n-           Cách xử lý các vết thương và chảy máu\r\n\r\n-           Cách xử lý chấn thương xương, cơ, khớp\r\n\r\n-           Cách kiểm soát các ảnh hưởng của nhiệt độ\r\n\r\n-           Cách xử trí dị vật, tình trạng ngộ độc, vết cắn & vết đốt\r\n\r\n-           Cách xử trí các tình trạng bệnh\r\n\r\n-           Hiểu và sử dụng đúng các kỹ thuật, dụng cụ\r\n\r\n-           Nắm vững sơ cấp cứu trong trường hợp khẩn cấp\r\n\r\n* Cấu trúc cuốn sách giúp người đọc có thể tra cứu, tìm hiểu nội dung, và áp dụng xử trí nhanh khi cần:\r\n\r\n- Phần chính: trình bày cách sơ cứu ban đầu cho 110 bệnh và thương tổn; tất cả đều đi kèm DẤU HIỆU NHẬN BIẾT và HƯỚNG DẪN XỬ TRÍ ban đầu.\r\n\r\n- Phần tham khảo cấp cứu nhanh: cung cấp kế hoạch hành động tức thì, tóm tắt cách xử trí cho những bệnh và thương tổn nguy hiểm đến tính mạng, từ bất tỉnh, chảy máu cho tới hen hay nhồi máu cơ tim…\r\n\r\n- Phần giải phẫu: kiến thức chung theo hệ cơ quan hoặc nguyên nhân gây thương tích, giải thích các rủi ro liên quan và các lợi ích mà sơ cấp cứu đem lại.\r\n\r\nĐây là cuốn sách thường thức, dễ tiếp cận, do đề cập đến những sự cố hiện hữu ở mọi nơi trong đời sống thường nhật (như hỏa hoạn, tai nạn giao thông…), và các tình trạng có thể gặp phải ở mọi người ở mọi độ tuổi (chảy máu, điện giật, đuối nước, bất tỉnh do nhiều nguyên nhân…).\r\n\r\nĐiểm lợi thế khác là cách trình bày, diễn giải ngắn gọn, súc tích và dễ hiểu, phù hợp cho mọi đối tượng: minh họa bằng hình ảnh người thật, nêu rõ từng bước thực hiện nhanh và chính xác.\r\n\r\nHy vọng đây sẽ là cuốn cẩm nang hữu ích, thiết thực đối với mọi cá nhân, gia đình và tổ chức.', '209300', '', '2dcd389a34e695a5f0106f57dfc6fb7184.jpeg', '', 0, NULL, NULL),
(45, 24, 6, 'How Food Works - Hiểu Hết Về Thức Ăn', '<p><strong>M&Ocirc; TẢ SẢN PHẨM</strong></p>\r\n\r\n<p><strong>T&aacute;c giả:</strong> DK</p>\r\n\r\n<p><strong>Người dịch: </strong>Trần Trương Ph&uacute;c Hạnh</p>\r\n\r\n<p><strong>Nh&agrave; xuất bản:</strong> Nxb Thế giới</p>\r\n\r\n<p><strong>Nh&agrave; ph&aacute;t h&agrave;nh: </strong>Nh&atilde; Nam</p>\r\n\r\n<p><strong>Định dạng: </strong>B&igrave;a cứng</p>\r\n\r\n<p><strong>K&iacute;ch thước</strong>: 19.5 x 23 cm</p>\r\n\r\n<p><strong>Ng&agrave;y ph&aacute;t h&agrave;nh:</strong> 12/2018</p>', '<p><strong>GIỚI THIỆU S&Aacute;CH</strong></p>\r\n\r\n<p>How Food Works - Hiểu Hết Về Thức Ăn Hướng dẫn đơn giản nhất v&agrave; trực quan nhất từ trước đến nay về thức ăn v&agrave; dinh dưỡng! C&oacute; chế độ ăn n&agrave;o l&agrave; ho&agrave;n hảo kh&ocirc;ng? Ch&uacute;ng ta c&oacute; cần uống 8 cốc nước mỗi ng&agrave;y? Ch&iacute;nh x&aacute;c th&igrave; gluten l&agrave; g&igrave; v&agrave; tại sao nhiều người muốn tr&aacute;nh xa n&oacute; đến vậy? Truyền th&ocirc;ng ngập tr&agrave;n những kh&aacute;m ph&aacute; v&agrave; những lời khuy&ecirc;n mới mẻ về những thứ ch&uacute;ng ta nạp v&agrave;o cơ thể nhưng c&oacute; cơ sở khoa học n&agrave;o cho bất kỳ tuy&ecirc;n bố đ&oacute; kh&ocirc;ng? Với những h&igrave;nh minh họa r&otilde; r&agrave;ng, dễ hiểu, cuốn s&aacute;ch Hiểu hết về thức ăn sẽ mang lại những th&ocirc;ng tin l&yacute; th&uacute; li&ecirc;n quan đến thức ăn, từ c&ocirc;ng đoạn sản xuất đến &iacute;ch lợi/t&aacute;c hại của ch&uacute;ng với sức khỏe.</p>', '231000', '201000', 'How Food Works - Hiểu Hết Về Thức Ăn34.jpeg', '', 1, NULL, NULL),
(46, 25, 6, 'Bách khoa thiếu nhi - Ai cập cổ đại', 'MÔ TẢ SẢN PHẨM\r\nTác giả: DK\r\nGiá bìa: 100.000 ₫\r\nNXB: NXB Phụ Nữ Việt Nam\r\nPhát hành: Minh Thắng\r\nNăm xuất bản: 2020 \r\nDịch giả: Lê Hải\r\nDạng bìa: bìa mềm\r\nSố trang: 160 trang\r\nKích thước: 13,5x16,5cm', '--------------Giới thiệu sách------------------\r\nBách khoa thiếu nhi - Ai cập cổ đại (bìa cứng)\r\n\r\n Vương triều Ai Cập ở Đông Bắc châu Phi hơn 3.000 năm trước để lại cho chúng ta những vị vua cổ đại, những kim tự tháp kỳ bí và phát minh ra giấy. Hãy cùng tìm hiểu về đất nước cổ đại này - những bộ luật, những vị thần hay cuộc sống dọc bờ sông Nin - tất cả có trong cuốn Bách khoa thiếu nhi thú vị này.\r\n\r\nHơn 180 điều thú vị về các vị vua Ai Cập cổ đại, các vị thần và những di tích cổ\r\nTái hiện các công trình, các kho báu khảo cổ qua những hình ảnh sắc nét, độ phân giải cao\r\nCác kiến thức được minh họa bằng ảnh mô hình khoa học\r\nNhững tri thức khoa học, kỷ lục thú vị và đáng quan tâm\r\nCùng tìm đọc trong bộ Bách khoa thiếu nhi: Ai Cập cổ đại * Động vật * Cơ thể người * Động vật có vú * Khoa học * Vũ trụ * Trái đất.', '100000', '89999', 'Bách khoa thiếu nhi - Ai cập cổ đại91.jpeg', '', 1, NULL, NULL),
(47, 25, 5, 'Bách khoa thiếu nhi - Trái Đất', 'MÔ TẢ SẢN PHẨM\r\nTác giả: DK\r\nGiá bìa: 100.000 ₫\r\nNXB: NXB Phụ Nữ Việt Nam\r\nPhát hành: Minh Thắng\r\nNăm xuất bản: 2020 \r\nDạng bìa: bìa cứng\r\nSố trang: 160 trang\r\nKích thước: 13,5 x 16,5cm', '------------------Giới thiệu sách-------------\r\nBách khoa thiếu nhi - Trái Đất (bìa cứng)\r\n\r\nTrái Đất, hành tinh xanh, ngòi nhà của những sa mạc khô cằn, những đình núi chọc trời, những đại dương sâu thẳm và những rừng rậm mênh mông. Hãy cùng khám phá vé Trái Đất - cùng tìm hiểu nguyên nhân của động đất; tìm xem dòng sòng băng rộng nhất nằm ở đâu hay tại sao khí hậu Trái Đất lại đang thay đổi - trong cuốn Bách khoa thiếu nhi thú vị này.\r\n\r\nHơn 180 điều thú vị về Trái Đất như núi lửa, sông ngòi và thời tiết\r\nHình ành minh họa sắc nét, độ phân giải cao\r\nCác kiến thức được minh họa bằng ảnh mô hình khoa học\r\nNhững tri thức, mốc lịch sử bất ngờ, thú vị và đáng quan tâm\r\nCùng tìm đọc trong bộ Bách khoa thiếu nhi: Ai Cập cồ đại * Động vật * Cơ thể người * Động vật có vú * Khoa học * Vũ trụ * Trái đất', '100000', '', 'Bách khoa thiếu nhi - Trái Đất12.jpeg', '', 0, NULL, NULL),
(48, 25, 6, 'Bách khoa thư về các loại phương tiện giao thông', 'MÔ TẢ SẢN PHẨM\r\nBách khoa thư về các loại phương tiện giao thông\r\nMã sản phẩm: 8935212351416\r\nTác giả : DK và Clive Gifford\r\nDịch giả : Thu Trang\r\nNXB Thanh Niên\r\nĐộ tuổi : 8 tuổi trở lên\r\nKích thước : 21x26.7cm\r\nNăm xuất bản : 2020\r\nSố trang : 256 trang + 4 trang gác', 'THÔNG TIN\r\nTrước khi những chiếc xe hơi, tàu hỏa, tàu thủy và máy bay xuất hiện, có rất ít người đi ra khỏi ngôi làng của họ, và càng có ít người dám vượt biển đi xa. Loài người không có cánh, nhưng chính trí tưởng tượng, óc sáng tạo không ngừng đã giúp chúng ta phát minh ra các loại phương tiện hiện đại để dễ dàng kết nối đến mọi nơi trên khắp Trái Đất, thậm chí là bay vào vũ trụ diệu kỳ.\r\n\r\nVới hơn 1.000 bức ảnh chụp tuyệt đẹp và những thông tin hấp dẫn, Bách khoa thư về các loại phương tiện giao thông sẽ mở ra một thế giới xe cộ đầy màu sắc cho bé thỏa sức khám phá, từ những loại xe gần gũi, quen thuộc như ô tô, xe máy, xe đạp đến các phương tiện bé ít thấy như tàu ngầm, xe quân sự, tàu sân bay. Tất cả những thắc mắc từ đơn giản đến phức tạp của bé, như “Ai là người phát minh ra chiếc xe đạp đầu tiên?”, “Chiếc máy bay tàng hình thế hệ mới nhất có tên là gì?” đến  “Loại phương tiện giao thông nào có vận tốc nhanh hơn cả âm thanh?” đều được tìm thấy trong cuốn sách tràn ngập các loại phương tiện giao thông này.\r\n\r\nCuốn sách được thiết kế khổ lớn, màu sắc bắt mắt, tranh ảnh sinh động và đầy công phu phù hợp với tâm lý và khả năng tiếp nhận của các bạn nhỏ, hứa hẹn sẽ đem lại những giây phút khám phá, tìm tòi và học hỏi thật say mê.\r\n\r\nNhững ước mơ của con trẻ đôi khi được nuôi dưỡng từ những điều nhỏ bé đơn giản, từ mô hình xe con say sưa lắp ghép hồi nhỏ, từ những câu chuyện đầy thú vị về lịch sử và hành trình của chiếc xe đạp, chiếc ô tô, chiếc máy bay. Không chỉ đơn thuần là câu chuyện về những chiếc xe, cuốn sách Bách khoa thư về các loại phương tiện giao thông còn truyền cảm hứng cho các cô bé, cậu bé đột phá các giới hạn, dám mơ lớn và không ngừng bay xa.\r\n\r\n Chuyến phiêu lưu kỳ thú trên những chiếc siêu xe hào nhoáng, chiếc du thuyền sang trọng, thậm chí là chiếc máy bay siêu thanh du ngoạn qua những vùng đất, vùng trời, vùng biển bao la đang chờ đón các bạn nhỏ.', '224000', '220000', 'Bách khoa thư về các loại phương tiện giao thông24.jpeg', '', 1, NULL, NULL),
(49, 25, 6, 'Bóc dán tuyệt đỉnh 6T', 'MÔ TẢ SẢN PHẨM\r\nTác giả: DK\r\nDịch giả: Thùy Dương\r\nNXB Thanh Niên\r\nĐộ tuổi: 3-9 tuổi\r\nKích thước: 20.5x26.5cm\r\nNăm xuất bản: 2021\r\nSố trang: 16 trang + 8 trang decan', '3-9 tuổi là giai đoạn bộ não của trẻ phát triển nhanh nhất để có thể nhận thức được màu sắc, hình khối, đồ vật, phát triển mạnh mẽ về cả ngôn ngữ, tư duy logic và khả năng sáng tạo... Cũng bởi thế mà trẻ cần có những \"công cụ\" phù hợp để có thể nâng cao được toàn diện những kỹ năng của mình. Bộ sách Bóc dán tuyệt đỉnh là một \"thiết kế\" đặc biệt phù hợp cho trẻ ở độ tuổi này. Bộ sách gồm 6 cuốn, bao hàm tổng quát các lĩnh vực phát triển tư duy cần thiết, sẽ giúp trẻ:\r\n\r\n* Phát huy tối đa khả năng nhận biết thế giới xung quanh, các hình khối, đồ vật.\r\n\r\n* Nâng cao khả năng sáng tạo và tư duy logic, giúp bé tạo ra những “tác phẩm” độc đáo mang cá tính riêng.\r\n\r\n* Nhận biết và phân biệt được chính xác giữa cơ thể người và động vật.\r\n\r\n* Rèn luyện khả năng ngôn ngữ linh hoạt, biết vận dụng ngôn ngữ thích hợp trong mỗi tình huống.\r\n\r\n* Rèn luyện thói quen sinh hoạt nề nếp và khoa học.\r\n\r\nBộ sách gồm các chủ đề : \r\n\r\n* Bóc dán tuyệt đỉnh - Các lá cờ trên thế giới\r\n\r\n* Bóc dán tuyệt đỉnh - Phương tiện giao thông\r\n\r\n* Bóc dán tuyệt đỉnh - Các loài thú cưng\r\n\r\n* Bóc dán tuyệt đỉnh - Khủng long\r\n\r\n* Bóc dán tuyệt đỉnh - Cuộc sống trong rừng\r\n\r\n* Bóc dán tuyệt đỉnh - Các em bé động vật', '47200', '39999', 'Bóc dán tuyệt đỉnh 6T74.jpeg', '', 1, NULL, NULL),
(50, 25, 6, 'Động Vật - Bách Khoa Thư Hình Ảnh Về Các Loài Vật Trên Trái Đất', 'MÔ TẢ SẢN PHẨM\r\nSách - Động Vật - Bách Khoa Thư Hình Ảnh Về Các Loài Vật Trên Trái Đất\r\n\r\nThông tin chi tiết\r\nCông ty phát hành	Đông A\r\nNgày xuất bản	09-2019\r\nTác giả: DK\r\nKích thước	21.5 x 27.5 cm\r\nLoại bìa	Bìa mềm\r\nSố trang	304\r\nSKU	2728286471878\r\nNhà xuất bản	Nhà Xuất Bản Dân Trí', 'Giới thiệu sách:\r\nĐộng Vật - Bách Khoa Thư Hình Ảnh Về Các Loài Vật Trên Trái Đất \r\n\r\nĐộng vật – Bách khoa thư hình ảnh về các loài vật trên Trái Đất bao gồm vô vàn thông tin và hình ảnh về những sinh vật tuyệt vời đang cư ngụ trên hành tinh xanh chúng ta, từ thực vật đến động vật, từ dưới đáy biển sâu đến trên đỉnh núi cao, từ loài vi khuẩn nhỏ bé đến loài voi khổng lồ. Với thông tin thú vị và dễ hiểu về hơn 1.500 loài, đây là cẩm nang thiết yếu cho các “nhà tự nhiên học tương lai” vừa học vừa chơi, vừa tìm hiểu những kiến thức bổ ích vừa thư giãn với những tranh ảnh tuyệt đẹp. \r\n\r\nMở đầu, “Cây sự sống” sẽ giúp độc giả bao quát toàn bộ thế giới sinh vật. Tiếp theo, mỗi chương về từng nhóm sinh vật đều có giới thiệu khái quát và tóm lược các đặc điểm chung. Sau đó, các loài sẽ được xếp vào các trang đôi, được trình bày theo tỉ lệ tương quan có kèm thước đo tỉ lệ. Các trang đôi đặc biệt sẽ cung cấp thêm thông tin về một số loài riêng biệt cũng như cho bạn ngắm nhìn chúng trong môi trường sống tự nhiên, chẳng hạn: ngựa vằn chiến đấu trông thế nào, hươu cao cổ uống nước ra sao, cá nhám voi săn mồi kiểu gì…', '300000', '289999', 'Động Vật Bách Khoa Thư Hình Ảnh Về Các Loài Vật Trên Trái Đất88.jpeg', '', 1, NULL, NULL),
(51, 25, 6, 'Look! - Tớ là Đầu bếp', 'MÔ TẢ SẢN PHẨM\r\nCông ty phát hành: Đinh Tị \r\nTác giả: DK\r\nNhà xuất bản: NXB Hà Nội \r\nNăm xuất bản: 2019 \r\nSố trang: 48', 'Giới thiệu sách:\r\nBạn vốn dĩ đã có đủ mọi tố chất để có thể trở thành một đầu bếp tuyệt vời: \r\n\r\nBỘ NÃO thông minh cùng những GIÁC QUAN siêu nhạy\r\n\r\nHãy sẵn sàng để CHẠM, NGỬI, NHÌN, NGHE VÀ NÊM NẾM những món ăn tuyệt vời theo cách của riêng bạn nhé.\r\n\r\nCuốn sách này sẽ giúp bạn tìm ra:\r\n\r\nLÀM CÁCH NÀO để chế biến một \"đoàn tàu\" salad thú vị?\r\n\r\nNHỮNG THÀNH PHẦN gì đã tạo nên món xốt bơ xanh mướt?\r\n\r\nTẠI SAO bánh mì lại phồng xốp như vậy?\r\n\r\nVà còn rất nhiều điều thú vị đang chờ bạn khám phá đấy.', '62000', '49999', 'Look! - Tớ là Đầu bếp7.jpeg', '', 1, NULL, NULL),
(52, 25, 6, 'Look... tớ là', 'MÔ TẢ SẢN PHẨM\r\nCó rất nhiều khả năng của bé mà bố mẹ có lẽ không thể biết nếu chúng ta không để các con thử va chạm và thực hành làm một vài hoạt động thú vị. Dưới đây là tuyển tập những hoạt động theo định hướng sự yêu thích của trẻ để chúng ta cùng thử nhé:', 'LOOK - TỚ LÀ ĐẦU BẾP\r\n\r\nCuốn sách với 11 công thức nấu ăn dễ làm và an toàn để các bé thoải mái thử \"lăn vào bếp\" đây. Gì chứ, thế giới bếp núc của mẹ rất tuyệt và có vô vàn điều bé sẽ học được đấy.\r\n\r\nVà biết đâu, gia đình mình sẽ có một \"vua đầu bếp?\"\r\n\r\nLOOK - TỚ LÀ KỸ SƯ\r\n\r\n15 dự án sáng tạo đã sẵn sàng chờ các bé. nào chúng ta cùng thiết kế một chiếc thuyền buồm từ hộp kem, hay làm bè cứu hộ từ que gỗ nhé. và bạn nào thích nghiên cứu sự hoạt động của máy bay không?...\r\n\r\nHãy mở cuốn sách này ra nhé những \"kỹ sư tương lai\"\r\n\r\nLOOK - TỚ LÀ NHÀ KHOA HỌC\r\n\r\nCác Nhà khoa học nhí chắc chắn sẽ mê mẩn cuốn sách này, vì trong này có tận 14 thí nghiệm khoa học thường thức cực kỳ bổ ích:\r\n- Nào hãy cùng tự tay làm tuyết từ bột nở và bọt cạo râu nhé,\r\n- Và ai đó đã làm được cả một bong bóng khổng lồ đủ để chúng ta chui vào kìa!!!\r\n\r\nBộ ba cuốn sách LOOK được mua bản quyền từ nước ngoài này đã tập hợp đầy đủ những hoạt động thực hành giúp khơi dậy trí tò mò về khoa học thiên bẩm và sự sáng tạo kỹ thuật của các bạn nhỏ. Sách phù hợp với các bạn từ 5 tuổi trở lên.\r\n#sachthieunhi #sachbanchay #kids #sachnuoidaycon #sachgiaoduc #sachhot #dochoi #sachtuongtac #danhinh #bocdan #tomau #sangtao', '150000', '123000', 'Look55.jpeg', '', 1, NULL, NULL),
(53, 25, 6, 'Tại sao Trái Đất cần Mặt Trăng', 'MÔ TẢ SẢN PHẨM\r\nThông tin\r\nMã sản phẩm: 8935212350945\r\nTác giả: DK\r\nDịch giả: Minh Trang\r\nNXB Hà Nội\r\nĐộ tuổi: 6 tuổi trở lên\r\nKích thước: 21.6x27.6cm\r\nNăm xuất bản: 2020\r\nSố trang: 144', 'Mặt Trăng là bạn đồng hành không thể thiếu của Trái Đất trong suốt hàng tỉ năm qua, nhưng tại sao một thiên thể cách xa chúng ta hàng trăm nghìn kilomet lại có vai trò quan trọng như vậy? Câu trả lời nằm ngay trong cuốn sách cực kỳ sinh động và thú vị này.\r\n\r\nVới lượng kiến thức khổng lồ và đa chiều, cuốn sách sẽ là con tàu kỳ diệu đưa các em đi khám phá tình bạn khăng khít giữa Trái Đất và Mặt Trăng, chiêm ngưỡng vẻ đẹp của không gian và những vì sao, ghé thăm những nơi sâu nhất, xa nhất, lạnh nhất của thế giới, thám hiểm sa mạc nóng bỏng và bầu trời cao vợi, từ đó hun đúc tình yêu dành cho khoa học và trên hết là tình yêu dành cho Trái Đất trong mỗi chúng ta.', '140000', '123000', 'Tại sao Trái Đất cần Mặt Trăng35.jpeg', '', 1, NULL, NULL);

